import React, { useEffect, useRef, useState } from 'react';
import { GoogleMap, useLoadScript, Marker } from 'vis.gl/react-google-map';
import { MarkerClusterer } from '@googlemaps/markerclusterer';

const MapComponent = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'TU_API_KEY_AQUI', // Reemplaza con tu clave de API
  });

  const [markers, setMarkers] = useState([
    { id: 1, lat: 4.43889, lng: -75.23222, title: 'Ubicación 1' },
    { id: 2, lat: 4.43989, lng: -75.24222, title: 'Ubicación 2' },
    { id: 3, lat: 4.44989, lng: -75.25222, title: 'Ubicación 3' },
  ]);

  const mapRef = useRef();

  const onLoad = (map) => {
    mapRef.current = map;
    const clusterer = new MarkerClusterer(map, markers.map(marker => {
      return new window.google.maps.Marker({
        position: { lat: marker.lat, lng: marker.lng },
        title: marker.title,
      });
    }), { imagePath: 'https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m' });

    return clusterer;
  };

  if (loadError) return <div>Error al cargar el mapa</div>;
  if (!isLoaded) return <div>Cargando...</div>;

  return (
    <div>
      <GoogleMap
        onLoad={onLoad}
        zoom={10}
        center={{ lat: 4.43889, lng: -75.23222 }}
        mapContainerStyle={{ width: '100%', height: '600px' }}
      >
        {markers.map(marker => (
          <Marker key={marker.id} position={{ lat: marker.lat, lng: marker.lng }} title={marker.title} />
        ))}
      </GoogleMap>
    </div>
  );
};

export default MapComponent;
