// src/components/MapComponent.jsx
import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import CoordenadasService from '../services/CoordenadasService';

const MapComponent = () => {
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [personas, setPersonas] = useState([]);
  const [selectedPersona, setSelectedPersona] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const fetchCoordenadas = async () => {
    try {
      const coordenadas = await CoordenadasService.getCoordenadas();
      console.log('Coordenadas recibidas:', coordenadas); 

      if (!coordenadas || coordenadas.length === 0) {
        console.error('No se recibieron coordenadas válidas.');
        return;
      }

      const markersData = coordenadas.map(coord => ({
        id: coord.id,
        marca: coord.marca,
        lat: parseFloat(coord.longitud), // Corrige esto
        lng: parseFloat(coord.latitud),   // Corrige esto
      }));

      console.log('Marcadores actuales:', markersData);
      setMarkers(markersData);
    } catch (error) {
      console.error('Error fetching coordinates:', error);
    }
  };

  useEffect(() => {
    
      const fetchData = async () => {
          const response = await fetch('http://localhost:9001/Laboratorio1/personas');
          const data = await response.json();
          setPersonas(data);
      };
    fetchData();
    fetchCoordenadas();
  }, []);

  const handleMarkerClick = (persona) => {
    setSelectedPersona(persona);
    setShowModal(true);
  };

  const handleCloseInfoWindow = () => {
    setSelectedMarker(null);
  };

  const center = markers.length > 0 ? { lat: markers[0].lat, lng: markers[0].lng } : { lat: 0, lng: 0 };

  return (
    <LoadScript googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc">
      <GoogleMap
        mapContainerStyle={{ height: '400px', width: '800px' }}
        center={center}
        zoom={6}
      >
        {personas.map(persona => (
          <Marker 
          key={persona.idpersona}
          position={{
              lat: persona.coordenadas.latitud,
              lng: persona.coordenadas.longitud
          }}
          onClick={() => handleMarkerClick(persona)}
          />
        ))}

        {selectedMarker && (
          <InfoWindow
            position={{ lat: selectedMarker.lat, lng: selectedMarker.lng }}
            onCloseClick={handleCloseInfoWindow}
          >
            <div>
            <h5>{selectedPersona.primerNombre} {selectedPersona.segundoNombre} {selectedPersona.primerApellido} {selectedPersona.segundoApellido}</h5>
                            <p>Identificación: {selectedPersona.identificacion}</p>
                            <p>Email: {selectedPersona.email}</p>
                            <p>Ubicación: {selectedPersona.ubicacion.direccionCompleta}</p>
                            <p>Edad Clínica: {selectedPersona.edadClinica}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
