import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import AuthService from '../services/AuthService'; // Asegúrate de la ruta correcta

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState(''); // Agregar el estado para la API Key
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError('');

        try {
            const response = await AuthService.authenticate(username, password, apiKey); // Usar el AuthService
            if (response && response.token) {
                console.log('Token generado:', response.token); // Mostrar el token en la consola
                localStorage.setItem('token', response.token);
                localStorage.setItem('apiKey', response.apikey);
                navigate('/home');
            } else {
                setError('Respuesta del servidor inválida');
            }
        } catch (error) {
            setError('Credenciales inválidas o error en la autenticación');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                {error && <div className="alert alert-danger">{error}</div>}
                <div>
                    <label htmlFor="username">Nombre de usuario:</label>
                    <InputText value={username} onChange={(e) => setUsername(e.target.value)} />
                </div>
                <div>
                    <label htmlFor="password">Contraseña:</label>
                    <InputText type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <div>
                    <label htmlFor="apikey">API Key:</label>
                    <InputText value={apiKey} onChange={(e) => setApiKey(e.target.value)} />
                </div>
                <Button type="submit" label="Iniciar Sesión" />
            </form>
        </div>
    );
};

export default Login;
