import React, { useEffect, useState } from 'react';
import { Button } from 'primereact/button';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {
    const [personas, setPersonas] = useState([]);

    useEffect(() => {
        const fetchPersonas = async () => {
            const response = await fetch('http://localhost:9001/Laboratorio1/persona');
            const data = await response.json();
            setPersonas(data);
        };

        fetchPersonas();
    }, []);

    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: '¿Estás seguro?',
            text: 'Esta acción no se puede deshacer.',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonText: 'Sí, eliminar',
            cancelButtonText: 'Cancelar'
        });

        if (result.isConfirmed) {
            const response = await fetch(`http://localhost:9001/Laboratorio1/persona/${id}`, {
                method: 'DELETE'
            });

            if (response.ok) {
                Swal.fire('Eliminado', 'La persona ha sido eliminada', 'success');
                setPersonas(personas.filter(persona => persona.id !== id));
            } else {
                Swal.fire('Error', 'No se pudo eliminar la persona', 'error');
            }
        }
    };

    return (
        <div className="container mt-5">
            <h2>Lista de Personas</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Identificación</th>
                        <th>Primer Nombre</th>
                        <th>Segundo Nombre</th>
                        <th>Primer Apellido</th>
                        <th>Segundo Apellido</th>
                        <th>Email</th>
                        <th>Edad</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {personas.map((persona) => (
                        <tr key={persona.id}>
                            <td>{persona.identificacion}</td>
                            <td>{persona.primerNombre}</td>
                            <td>{persona.segundoNombre}</td>
                            <td>{persona.primerApellido}</td>
                            <td>{persona.segundoApellido}</td>
                            <td>{persona.email}</td>
                            <td>{persona.edad}</td>
                            <td>
                                <Button
                                    label="Actualizar"
                                    className="mr-2"
                                    style={{
                                        backgroundColor: '#007bff',
                                        borderColor: '#007bff',
                                        transition: 'background-color 0.3s ease'
                                    }}
                                    onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#28a745')}
                                    onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#007bff')}
                                    onClick={() => {/* Redirigir a UpdatePerson con el ID de la persona */}}
                                />
                                <Button
                                    label="Eliminar"
                                    className="p-button-danger"
                                    style={{
                                        backgroundColor: '#dc3545',
                                        borderColor: '#dc3545',
                                        transition: 'background-color 0.3s ease'
                                    }}
                                    onMouseEnter={(e) => (e.currentTarget.style.backgroundColor = '#ff6b6b')}
                                    onMouseLeave={(e) => (e.currentTarget.style.backgroundColor = '#dc3545')}
                                    onClick={() => handleDelete(persona.id)}
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Home;
