import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import bcrypt from 'bcryptjs'; // Asegúrate de instalar bcryptjs
import PersonaService from "../services/PersonaService";

const UpdatePerson = () => {
    const { id } = useParams(); // Obtener el ID de los parámetros de la URL
    const navigate = useNavigate(); // Para navegar a otras rutas

    // Estado inicial de la persona
    const [persona, setPersona] = useState({
        id: id,
        primerNombre: '',
        segundoNombre: '',
        primerApellido: '',
        segundoApellido: '',
        identificacion: '',
        email: '',
        fechaNacimiento: '',
        ubicacion: {
            lugar: '',
            ciudad: '',
            departamento: '',
        },
        usuario: {
            login: '',
            password: ''
        }
    });

    // Cargar los datos de la persona
    useEffect(() => {
        const loadPersona = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                Swal.fire('Error', 'No se ha encontrado el token de autenticación.', 'error');
                navigate('/login');
                return;
            }
            try {
                const data = await PersonaService.getPersonById(id); // Usar el servicio para obtener la persona
                setPersona(data);
            } catch (error) {
                console.error('Error al cargar persona:', error);
                Swal.fire('Error', 'No se pudo cargar la persona.', 'error');
                navigate('/home');
            }
        };

        loadPersona();
    }, [id, navigate]);

    // Manejo de cambios en los campos de entrada
    const handleChange = (e) => {
        const { name, value } = e.target;
        setPersona(prevPersona => ({
            ...prevPersona,
            [name]: value,
        }));
    };

    const handleUbicacionChange = (e) => {
        const { name, value } = e.target;
        setPersona(prevPersona => ({
            ...prevPersona,
            ubicacion: {
                ...prevPersona.ubicacion,
                [name]: value,
            }
        }));
    };

    const handleUsuarioChange = (e) => {
        const { name, value } = e.target;
        setPersona(prevPersona => ({
            ...prevPersona,
            usuario: {
                ...prevPersona.usuario,
                [name]: value,
            }
        }));
    };

    // Manejo del envío del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');
        if (!token) {
            Swal.fire('Error', 'No se ha encontrado el token de autenticación.', 'error');
            navigate('/login');
            return;
        }

        // Encriptar la contraseña antes de enviar
        const hashedPassword = bcrypt.hashSync(persona.usuario.password, 10);
        const personaConPasswordHasheado = {
            ...persona,
            usuario: {
                ...persona.usuario,
                password: hashedPassword,
            },
        };

        try {
            const response = await PersonaService.updatePerson(persona.id, personaConPasswordHasheado); // Usar el servicio para actualizar
            Swal.fire('Actualizado', 'Los datos han sido actualizados correctamente.', 'success');
            navigate('/home');
        } catch (error) {
            console.error('Error al actualizar persona:', error);
            Swal.fire('Error', error.response?.data.message || 'No se pudo actualizar los datos.', 'error');
        }
    };

    // Manejo del botón de cancelar
    const handleCancel = () => {
        navigate('/home');
    };

    return (
        <div className="container mt-5" style={{ background: 'linear-gradient(to right, #c1d3ff, #c9b1ff)', minHeight: '100vh' }}>
            <h2>Actualizar Persona</h2>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Primer Nombre</label>
                    <input type="text" className="form-control" name="primerNombre" value={persona.primerNombre} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Segundo Nombre</label>
                    <input type="text" className="form-control" name="segundoNombre" value={persona.segundoNombre} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label className="form-label">Primer Apellido</label>
                    <input type="text" className="form-control" name="primerApellido" value={persona.primerApellido} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Segundo Apellido</label>
                    <input type="text" className="form-control" name="segundoApellido" value={persona.segundoApellido} onChange={handleChange} />
                </div>
                <div className="mb-3">
                    <label className="form-label">Identificación</label>
                    <input type="text" className="form-control" name="identificacion" value={persona.identificacion} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input type="email" className="form-control" name="email" value={persona.email} onChange={handleChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Fecha de Nacimiento</label>
                    <input type="date" className="form-control" name="fechaNacimiento" value={persona.fechaNacimiento} onChange={handleChange} required />
                </div>
                
                <h5>Ubicación</h5>
                <div className="mb-3">
                    <label className="form-label">Lugar</label>
                    <input type="text" className="form-control" name="lugar" value={persona.ubicacion.lugar} onChange={handleUbicacionChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Ciudad</label>
                    <input type="text" className="form-control" name="ciudad" value={persona.ubicacion.ciudad} onChange={handleUbicacionChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Departamento</label>
                    <input type="text" className="form-control" name="departamento" value={persona.ubicacion.departamento} onChange={handleUbicacionChange} required />
                </div>
                <h5>Usuario</h5>
                <div className="mb-3">
                    <label className="form-label">Login</label>
                    <input type="text" className="form-control" name="login" value={persona.usuario.login} onChange={handleUsuarioChange} required />
                </div>
                <div className="mb-3">
                    <label className="form-label">Contraseña</label>
                    <input type="password" className="form-control" name="password" value={persona.usuario.password} onChange={handleUsuarioChange} required />
                </div>
                <div className="d-flex justify-content-between">
                    <button type="submit" className="btn" style={{ backgroundColor: '#4CAF50', color: 'white' }}>Actualizar</button>
                    <button type="button" className="btn" style={{ backgroundColor: '#6c757d', color: 'white' }} onClick={handleCancel}>Cancelar</button>
                </div>
            </form>
        </div>
    );
};

export default UpdatePerson;
