import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Card, Button, InputText } from 'primereact';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [apiKey, setApiKey] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchApiKey(); 
    }, [username]);

    const fetchApiKey = async () => {
        if (username) {
            try {
                const response = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        username: username
                    }
                });
                setApiKey(response.data.apiKey);
                setError(''); 
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
                setError('No se pudo recuperar la API Key');
            }
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        if (!apiKey) {
            setError('No se pudo recuperar la API Key');
            return;
        }

        try {
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: {
                    apikey: apiKey
                }
            });
            localStorage.setItem('token', response.data.token);
            navigate('/home');
        } catch (error) {
            setError('Credenciales inválidas');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container-fluid vh-100 d-flex align-items-center justify-content-center" style={{ background: 'linear-gradient(135deg, #74ebd5 0%, #9face6 100%)' }}>
            <Card title="Iniciar Sesión" style={{ width: '25rem' }} className="p-fluid shadow-lg">
                <form onSubmit={handleSubmit}>
                    {error && <div className="alert alert-danger">{error}</div>}
                    <div className="mb-3">
                        <label htmlFor="username" className="form-label">Nombre de usuario</label>
                        <InputText
                            type="text"
                            id="username"
                            placeholder="Ingresa tu nombre de usuario"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                            className="w-100"
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Contraseña</label>
                        <InputText
                            type="password"
                            id="password"
                            placeholder="Ingresa tu contraseña"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="w-100"
                        />
                    </div>
                    <Button type="submit" label="Iniciar Sesión" className="p-button-success w-100 mt-3" />
                    <Button 
                        type="button" 
                        label="Registrarse" 
                        className="p-button-link mt-3" 
                        onClick={() => navigate('/register')}
                    />
                </form>
            </Card>
        </div>
    );
};

export default Login;
