import React, { useEffect, useState } from 'react';
import Swal from 'sweetalert2';
import PersonaService from '../services/PersonaService'; // Asegúrate de que la ruta sea correcta

const UpdatePerson = () => {
    const [personas, setPersonas] = useState([]);

    useEffect(() => {
        loadPersonas();
    }, []);

    const loadPersonas = async () => {
        try {
            const response = await PersonaService.getAll(); // Asumiendo que tienes este método en tu servicio
            setPersonas(response.data);
        } catch (error) {
            console.error("Error al cargar las personas:", error);
        }
    };

    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: '¿Estás seguro?',
            text: '¡No podrás recuperar este registro!',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: '¡Sí, eliminar!'
        });

        if (result.isConfirmed) {
            try {
                const token = localStorage.getItem('token'); // Obtener el token de localStorage
                const response = await fetch(`http://localhost:9001/Laboratorio1/personas/${id}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.ok) {
                    console.log("Persona eliminada con éxito.");
                    loadPersonas(); // Recargar la lista después de eliminar
                    Swal.fire('Eliminado', 'El registro ha sido eliminado.', 'success');
                } else {
                    console.error("Error al eliminar la persona.");
                    Swal.fire('Error', 'No se pudo eliminar el registro.', 'error');
                }
            } catch (error) {
                console.error("Error en la solicitud:", error);
                Swal.fire('Error', 'No se pudo eliminar el registro.', 'error');
            }
        } else {
            console.log("Eliminación cancelada.");
        }
    };

    return (
        <div>
            <h2>Lista de Personas</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {personas.map((persona) => (
                        <tr key={persona.id}>
                            <td>{persona.id}</td>
                            <td>{persona.nombre}</td>
                            <td>
                                <button onClick={() => handleDelete(persona.id)}>Eliminar</button>
                                {/* Aquí puedes añadir otros botones para editar o ver detalles */}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UpdatePerson;
