const API_URL = 'http://localhost:9001/Laboratorio1';

const handleResponse = async (response) => {
  if (!response.ok) {
    const errorText = await response.text();
    throw new Error(`Error en la solicitud: ${response.status} ${response.statusText} - ${errorText}`);
  }
  return response.json();
};

const PersonaService = {
  getHeaders: () => {
    return {
      'Content-Type': 'application/json',
    };
  },

  getAllPersons: async () => {
    try {
      const response = await fetch(`${API_URL}/personas`, {
        method: 'GET',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al obtener personas:', error);
      throw error;
    }
  },

  getPersonById: async (id) => {
    try {
      const response = await fetch(`${API_URL}/personas/${id}`, {
        method: 'GET',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error al obtener persona con ID ${id}:`, error);
      throw error;
    }
  },

  registerUser: async (personData) => {
    try {
      const response = await fetch(`${API_URL}/register`, {  // Asegúrate de que este endpoint sea el correcto
        method: 'POST',
        headers: PersonaService.getHeaders(),
        body: JSON.stringify(personData),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al registrar usuario:', error);
      throw error;
    }
  },

  createPerson: async (personData) => {
    try {
      const response = await fetch(`${API_URL}/persona`, {
        method: 'POST',
        headers: PersonaService.getHeaders(),
        body: JSON.stringify(personData),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al crear persona:', error);
      throw error;
    }
  },

  updatePerson: async (id, personData) => {
    try {
      const response = await fetch(`${API_URL}/personas/${id}`, {
        method: 'PUT',
        headers: PersonaService.getHeaders(),
        body: JSON.stringify(personData),
      });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error al actualizar persona con ID ${id}:`, error);
      throw error;
    }
  },

  deletePerson: async (id) => {
    try {
      const response = await fetch(`${API_URL}/personas/${id}`, {
        method: 'DELETE',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error al eliminar persona con ID ${id}:`, error);
      throw error;
    }
  },

  fetchCoordinates: async (ids) => {
    try {
      const queryParams = ids.map(id => `ids=${id}`).join('&');
      const response = await fetch(`${API_URL}/coordenadas/lista?${queryParams}`, {
        method: 'GET',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al obtener coordenadas:', error);
      throw error;
    }
  },
};

export default PersonaService;
