/* global google */

import React, { useEffect, useRef } from 'react';

const MapComponent = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const loadGoogleMaps = () => {
      const script = document.createElement('script');
      // Aquí se carga la librería de Google Maps con las librerías necesarias
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc&libraries=marker`;
      script.async = true;
      script.defer = true;
      script.onload = () => {
        // Llamamos a la función que inicializa el mapa aquí
        initializeMap();
      };
      document.head.appendChild(script);
    };

    const initializeMap = () => {
      if (typeof google !== 'undefined') {
        const map = new google.maps.Map(mapRef.current, {
          center: { lat: -34.397, lng: 150.644 },
          zoom: 8,
        });

        // Usar AdvancedMarkerElement
        const marker = new google.maps.marker.AdvancedMarkerElement({
          position: { lat: -34.397, lng: 150.644 },
          map,
          title: "Marcador Avanzado",
        });
      } else {
        console.error('Google Maps API no se ha cargado correctamente');
      }
    };

    loadGoogleMaps();
  }, []);

  return (
    <div>
      <h2>Mapa de Google con AdvancedMarkerElement</h2>
      <div ref={mapRef} style={{ height: '500px', width: '100%' }}></div>
    </div>
  );
};

export default MapComponent;
