const API_URL = 'http://localhost:9001/Laboratorio1';

const handleResponse = async (response) => {
  if (!response.ok) {
    const errorText = await response.text();
    console.error('Server response:', errorText);
    throw new Error(`Error en la solicitud: ${response.status} - ${errorText}`);
  }
  return response.json();
};

const PersonaService = {
  getHeaders: () => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No se encontró un token. Inicia sesión nuevamente.');
    }
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`,
    };
  },

  getHeadersForRegister: () => ({
    'Content-Type': 'application/json',
  }),

  fetchData: async (url, method, headers, body) => {
    try {
      const response = await fetch(url, { method, headers, body });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error en la solicitud ${method} a ${url}:`, error);
      throw error;
    }
  },

  getAllPersons: () => 
    this.fetchData(`${API_URL}/personas`, 'GET', this.getHeaders()),

  getPersonById: (id) => 
    this.fetchData(`${API_URL}/persona/id/${id}`, 'GET', this.getHeaders()),

  registerUser: async (personData) => {
    try {
      console.log('Sending data:', JSON.stringify(personData, null, 2));
      const response = await fetch(`${API_URL}/persona`, {
        method: 'POST',
        headers: this.getHeadersForRegister(),
        body: JSON.stringify(personData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        if (errorText.includes("nombre de usuario ya está registrado")) {
          throw new Error("El nombre de usuario ya está registrado");
        } else if (errorText.includes("correo ya está registrado")) {
          throw new Error("El correo electrónico ya está registrado");
        } else if (errorText.includes("identificación ya está registrada")) {
          throw new Error("La identificación ya está registrada");
        } else {
          throw new Error(errorText);
        }
      }
      
      return handleResponse(response);
    } catch (error) {
      console.error('Error al registrar usuario:', error);
      throw error;
    }
  },

  createPerson: (personData) => 
    this.fetchData(`${API_URL}/persona`, 'POST', this.getHeaders(), JSON.stringify(personData)),

  updatePerson: (id, personData) => 
    this.fetchData(`${API_URL}/persona`, 'PUT', this.getHeaders(), JSON.stringify({ ...personData, idpersona: id })),

  deletePerson: async (id) => {
    try {
      const response = await fetch(`${API_URL}/persona/${id}`, {
        method: 'DELETE',
        headers: this.getHeaders(),
      });
      if (!response.ok) {
        const errorText = await response.text();
        if (response.status === 403) {
          throw new Error('No tienes permiso para eliminar esta persona.');
        } else if (response.status === 500 && errorText.includes("Error al eliminar la persona y sus datos asociados")) {
          throw new Error('Hubo un problema al eliminar la persona y sus datos asociados.');
        } else {
          throw new Error(`Error al eliminar persona: ${response.status} - ${errorText}`);
        }
      }
      return true;
    } catch (error) {
      console.error(`Error al eliminar persona con ID ${id}:`, error);
      throw error;
    }
  },

  checkUsernameAvailability: (username) => 
    this.fetchData(`${API_URL}/check-username/${username}`, 'GET', this.getHeadersForRegister()),

  checkEmailAvailability: (email) => 
    this.fetchData(`${API_URL}/check-email/${email}`, 'GET', this.getHeadersForRegister()),

  checkIdentificationAvailability: (identification) => 
    this.fetchData(`${API_URL}/check-identification/${identification}`, 'GET', this.getHeadersForRegister()),
};

export default PersonaService;
