import React, { useEffect, useState, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import PersonaService from '../services/PersonaService';
import { Loader } from '@googlemaps/js-api-loader';
import 'bootstrap/dist/css/bootstrap.min.css';

const MapComponent = () => {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // Crear el Loader una sola vez
  const loader = useMemo(() => new Loader({
    apiKey: "AIzaSyBtmSbtAkZMgopeMNzXW4NC4LyzXRLr-ck",
    version: "weekly",
  }), []);

  useEffect(() => {
    PersonaService.getAllPersons()
      .then(data => {
        if (data) {
          setPersonas(data);
        }
      })
      .catch(error => console.error('Error fetching personas', error));
  }, []);

  const initMap = useCallback(() => {
    loader.load().then(() => {
      if (!map) {
        const mapInstance = new window.google.maps.Map(document.getElementById('map'), {
          center: { lat: 4.43889, lng: -75.23222 },
          zoom: 10,
        });
        setMap(mapInstance);
      }
    }).catch(error => {
      console.error('Error loading Google Maps:', error);
    });
  }, [loader, map]);

  useEffect(() => {
    initMap();
  }, [initMap]);

  useEffect(() => {
    if (map && selectedPersonas.length > 0) {
      markers.forEach(marker => marker.setMap(null));
      
      PersonaService.fetchCoordinates(selectedPersonas.map(p => p.id))
        .then(coordinates => {
          const bounds = new window.google.maps.LatLngBounds();
          const newMarkers = coordinates.map(coord => {
            const lat = parseFloat(coord.latitud);
            const lng = parseFloat(coord.longitud);
            
            if (isNaN(lat) || isNaN(lng)) {
              console.error('Coordenadas inv치lidas:', coord);
              return null;
            }

            const position = { lat, lng };
            bounds.extend(position);

            const AdvancedMarkerElement = window.google.maps.marker?.AdvancedMarkerElement;
            if (AdvancedMarkerElement) {
              return new AdvancedMarkerElement({
                position,
                map,
                title: coord.nombre || 'Desconocido',
              });
            } else {
              return new window.google.maps.Marker({
                position,
                map,
                title: coord.nombre || 'Desconocido',
              });
            }
          }).filter(marker => marker !== null);

          setMarkers(newMarkers);

          if (newMarkers.length > 0) {
            map.fitBounds(bounds);
            setTimeout(() => {
              window.google.maps.event.trigger(map, 'resize');
            }, 100);
          } else {
            console.warn('No se encontraron coordenadas v치lidas');
          }
        })
        .catch(error => console.error('Error al obtener coordenadas', error));
    }
  }, [map, selectedPersonas]);

  const handleSelectChange = (e) => {
    const selectedIds = Array.from(e.target.selectedOptions, option => option.value);
    const selectedPersons = personas.filter(persona => selectedIds.includes(persona.id.toString()));
    setSelectedPersonas(selectedPersons);
  };

  const handleSelectAll = () => {
    setSelectedPersonas(personas);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredPersonas = personas.filter(persona => 
    `${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Mapa de Ubicaciones</h2>
      <div className="row">
        <div className="col-md-8">
          <div id="map" style={{ height: '600px', width: '100%' }}></div>
        </div>
        <div className="col-md-4">
          <div className="form-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Buscar por nombre completo"
              value={searchTerm}
              onChange={handleSearch}
            />
          </div>
          <div className="form-group mb-3">
            <label htmlFor="personasSelect">Seleccione personas:</label>
            <select 
              id="personasSelect" 
              className="form-control" 
              multiple 
              onChange={handleSelectChange}
              size="10"
              value={selectedPersonas.map(p => p.id.toString())}
            >
              {filteredPersonas.map(persona => (
                <option key={persona.id} value={persona.id}>
                  {`${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`}
                </option>
              ))}
            </select>
          </div>
          <button className="btn btn-secondary mb-3 me-2" onClick={handleSelectAll}>
            Seleccionar Todos
          </button>
          <button className="btn btn-primary mb-3" onClick={() => navigate('/home')}>
            Volver a Home
          </button>
        </div>
      </div>
    </div>
  );
};

export default MapComponent;
