import React, { useEffect, useState } from 'react'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import PersonaService from '../services/PersonaService';

const MapComponent = () => {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    PersonaService.getAllPersons()
      .then(data => {
        // Asegúrate de que 'data' no sea nulo antes de establecer el estado
        if (data) {
          setPersonas(data);
        }
      })
      .catch(error => console.log('Error fetching personas', error));
  }, []);

  useEffect(() => {
    const initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: 4.43889, lng: -75.23222 },
        zoom: 10,
      });

      selectedPersonas.forEach(persona => {
        const marker = new window.google.maps.Marker({
          position: { lat: persona.latitud, lng: persona.longitud },
          map,
          title: persona.nombre,
        });
      });
    };

    if (window.google) {
      initMap();
    } else {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBtmSbtAkZMgopeMNzXW4NC4LyzXRLr-ck&callback=initMap`;
      script.defer = true;
      window.initMap = initMap;
      document.head.appendChild(script);
    }

    return () => {
      const script = document.querySelector(`script[src^="https://maps.googleapis.com/maps/api/js"]`);
      if (script) {
        document.head.removeChild(script);
      }
    };
  }, [selectedPersonas]);

  const handleSelectChange = (e) => {
    const selectedIds = Array.from(e.target.selectedOptions, option => option.value);
    const selectedPersons = personas.filter(persona => selectedIds.includes(persona.id.toString()));
    setSelectedPersonas(selectedPersons);
  };

  return (
    <div className="container-fluid h-100 d-flex flex-column">
      <div className="row flex-grow-1">
        <div id="map" className="col-12 h-100"></div>
      </div>

      <div className="form-group">
        <label htmlFor="personasSelect">Seleccione personas para ver sus ubicaciones:</label>
        <select id="personasSelect" className="form-control" multiple onChange={handleSelectChange}>
          {personas.map(persona => (
            <option key={persona.id} value={persona.id}>
              {persona.nombre}
            </option>
          ))}
        </select>
      </div>

      <button className="btn btn-primary mt-3" onClick={() => navigate('/home')}>Volver a Home</button>
    </div>
  );
};

export default MapComponent;
