import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import AuthService from '../services/AuthService'; // Asegúrate de que la ruta es correcta

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError('');

        try {
            const response = await AuthService.authenticate(username, password); // No pasa apiKey
            if (response && response.token) {
                console.log('Token generado:', response.token); // Mostrar el token en la consola
                localStorage.setItem('token', response.token);
                localStorage.setItem('apiKey', response.apikey); // Guarda la apiKey desde la respuesta
                navigate('/home');
            } else {
                setError('Respuesta del servidor inválida');
            }
        } catch (error) {
            setError('Credenciales inválidas o error en la autenticación');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit}>
                {error && <div className="alert alert-danger">{error}</div>}
                <div>
                    <label htmlFor="username">Nombre de usuario:</label>
                    <InputText
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password">Contraseña:</label>
                    <InputText
                        id="password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <Button type="submit" label="Iniciar Sesión" />
            </form>
        </div>
    );
};

export default Login;
