import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import AuthService from '../services/AuthService'; // Asegúrate de que la ruta sea correcta
import 'primereact/resources/themes/saga-blue/theme.css'; // Importa el tema de PrimeReact
import 'primereact/resources/primereact.min.css'; // Importa los estilos de PrimeReact
import 'primeicons/primeicons.css'; // Importa los iconos de PrimeReact

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError('');

        try {
            const response = await AuthService.authenticate(username, password);
            if (response && response.token) {
                console.log('Token generado:', response.token);
                localStorage.setItem('token', response.token);
                localStorage.setItem('apiKey', response.apikey); // Guarda la apiKey desde la respuesta
                navigate('/home'); // Redirige a la página de inicio
            } else {
                setError('Respuesta del servidor inválida');
            }
        } catch (error) {
            setError('Credenciales inválidas o error en la autenticación');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Iniciar Sesión</h2>
            <form onSubmit={handleSubmit} className="p-fluid">
                {error && <div className="alert alert-danger">{error}</div>}
                <div className="mb-3">
                    <label htmlFor="username">Nombre de usuario:</label>
                    <InputText
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        className="w-full"
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="password">Contraseña:</label>
                    <InputText
                        id="password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        className="w-full"
                    />
                </div>
                <Button type="submit" label="Iniciar Sesión" className="w-full" />
            </form>
        </div>
    );
};

export default Login;
