import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import PersonaService from "../services/PersonaService";

function Register() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    primerNombre: "",
    segundoNombre: "",
    primerApellido: "",
    segundoApellido: "",
    identificacion: "",
    email: "",
    fechaNacimiento: "",
    ubicacion: {
      lugar: "",
      ciudad: "",
      departamento: "",
    },
    usuario: {
      login: "",
      password: "",
    },
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [objectName, field] = name.split(".");
      setFormData((prevData) => ({
        ...prevData,
        [objectName]: {
          ...prevData[objectName],
          [field]: value,
        },
      }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const currentDate = new Date();
    const selectedDate = new Date(formData.fechaNacimiento);
    const year = selectedDate.getFullYear();

    if (selectedDate > currentDate) {
      setModalMessage("La fecha de nacimiento no puede ser futura.");
      setModalType("error");
      setShowModal(true);
      return;
    }

    if (year < 1900 || year > 2100) {
      setModalMessage("El año de nacimiento debe estar entre 1900 y 2100.");
      setModalType("error");
      setShowModal(true);
      return;
    }

    try {
      // Check availability of username, email, and identification
      await PersonaService.checkUsernameAvailability(formData.usuario.login);
      await PersonaService.checkEmailAvailability(formData.email);
      await PersonaService.checkIdentificationAvailability(formData.identificacion);

      const dataToSend = {
        ...formData,
        identificacion: parseInt(formData.identificacion),
        usuario: {
          ...formData.usuario,
          id: {
            login: formData.usuario.login,
            persona: null,
          },
        },
      };

      await PersonaService.registerUser(dataToSend);
      setModalMessage("Registro exitoso. Ahora puedes iniciar sesión.");
      setModalType("success");
      setShowModal(true);
    } catch (error) {
      console.error("Error:", error);
      setModalMessage(error.message || "Error en el registro. Por favor, intenta nuevamente.");
      setModalType("error");
      setShowModal(true);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    if (modalType === "success") {
      setFormData({
        primerNombre: "",
        segundoNombre: "",
        primerApellido: "",
        segundoApellido: "",
        identificacion: "",
        email: "",
        fechaNacimiento: "",
        ubicacion: {
          lugar: "",
          ciudad: "",
          departamento: "",
        },
        usuario: {
          login: "",
          password: "",
        },
      });
      navigate("/login");
    }
  };

  return (
    <div className="container mt-5" style={{ background: "linear-gradient(to right, #ff7e5f, #bdb6b0)", padding: "20px", borderRadius: "15px" }}>
      <div className="card shadow-lg border-light">
        <div className="card-header text-center">
          <h2 className="text-dark">Registrar Persona</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Nombre:</label>
                <input
                  type="text"
                  name="primerNombre"
                  className="form-control"
                  value={formData.primerNombre}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Nombre:</label>
                <input
                  type="text"
                  name="segundoNombre"
                  className="form-control"
                  value={formData.segundoNombre}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Apellido:</label>
                <input
                  type="text"
                  name="primerApellido"
                  className="form-control"
                  value={formData.primerApellido}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Apellido:</label>
                <input
                  type="text"
                  name="segundoApellido"
                  className="form-control"
                  value={formData.segundoApellido}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Identificación:</label>
                <input
                  type="number"
                  name="identificacion"
                  className="form-control"
                  value={formData.identificacion}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Fecha de Nacimiento:</label>
                <input
                  type="date"
                  name="fechaNacimiento"
                  className="form-control"
                  value={formData.fechaNacimiento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3 className="mt-4">Ubicación</h3>
            <div className="row mb-3">
              <div className="col-md-4">
                <label className="form-label">Lugar:</label>
                <input
                  type="text"
                  name="ubicacion.lugar"
                  className="form-control"
                  value={formData.ubicacion.lugar}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-4">
                <label className="form-label">Ciudad:</label>
                <input
                  type="text"
                  name="ubicacion.ciudad"
                  className="form-control"
                  value={formData.ubicacion.ciudad}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-4">
                <label className="form-label">Departamento:</label>
                <input
                  type="text"
                  name="ubicacion.departamento"
                  className="form-control"
                  value={formData.ubicacion.departamento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3 className="mt-4">Usuario</h3>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Login:</label>
                <input
                  type="text"
                  name="usuario.login"
                  className="form-control"
                  value={formData.usuario.login}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Password:</label>
                <input
                  type="password"
                  name="usuario.password"
                  className="form-control"
                  value={formData.usuario.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Guardar
              </button>
              <button type="button" className="btn btn-secondary" onClick={() => navigate("/login")}>
                Regresar a Login
              </button>
            </div>
          </form>
          {showModal && (
            <div className={`modal fade show ${modalType}`} style={{ display: 'block' }} tabIndex="-1">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">{modalType === "success" ? "Éxito" : "Error"}</h5>
                    <button type="button" className="btn-close" onClick={handleCloseModal}></button>
                  </div>
                  <div className="modal-body">
                    <p>{modalMessage}</p>
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                      Cerrar
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Register;
