/* global google */
import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Asegúrate de instalar axios

const MapComponent = () => {
  const [coordenadas, setCoordenadas] = useState([]); // Estado para almacenar coordenadas

  useEffect(() => {
    // Cargar Google Maps después de definir initializeMap
    loadGoogleMaps();
    fetchCoordenadas(); // Llama a la función para obtener coordenadas
  }, []);

  const loadGoogleMaps = () => {
    // Definir initializeMap en el contexto global
    window.initializeMap = initializeMap;

    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc&libraries=marker&callback=initializeMap`;
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);
  };

  const fetchCoordenadas = async () => {
    try {
      const response = await axios.get('http://localhost:9001/Laboratorio1/coordenadas');
      setCoordenadas(response.data); // Guarda las coordenadas en el estado
    } catch (error) {
      console.error("Error al obtener coordenadas:", error);
    }
  };

  const initializeMap = () => {
    const mapElement = document.getElementById('map');
    const map = new google.maps.Map(mapElement, {
      center: { lat: -34.397, lng: 150.644 }, // Establece un centro predeterminado
      zoom: 8,
    });

    // Añade marcadores para cada coordenada obtenida
    coordenadas.forEach(coordenada => {
      const marker = new google.maps.Marker({
        position: { lat: coordenada.latitud, lng: coordenada.longitud }, // Asume que las coordenadas tienen propiedades latitud y longitud
        map: map,
        title: 'Ubicación',
      });
    });
  };

  return (
    <div>
      <h1>Mi Mapa</h1>
      <div id="map" style={{ height: '500px', width: '100%' }}></div>
    </div>
  );
};

export default MapComponent;
