import React, { useEffect, useRef, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import CoordenadasService from '../services/CoordenadasService'; // Asegúrate de importar el servicio

const MapComponent = () => {
  const [markers, setMarkers] = useState([]);
  const mapRef = useRef(null);

  const fetchCoordenadas = async () => {
    try {
      const coordenadas = await CoordenadasService.getCoordenadas();
      // Transformar datos para usarlos como marcadores
      const markersData = coordenadas.map(coord => ({
        id: coord.id,
        marca: coord.marca,
        lat: coord.latitud,
        lng: coord.longitud,
      }));
      setMarkers(markersData);
    } catch (error) {
      console.error('Error fetching coordinates:', error);
    }
  };

  useEffect(() => {
    fetchCoordenadas();
  }, []);

  return (
    <LoadScript googleMapsApiKey="TU_API_KEY">
      <GoogleMap
        mapContainerStyle={{ height: '400px', width: '800px' }}
        center={{ lat: 4.610, lng: -74.083 }} // Ajusta al centro deseado
        zoom={6} // Ajusta el nivel de zoom según sea necesario
      >
        {markers.map(marker => (
          <Marker 
            key={marker.id} 
            position={{ lat: marker.lat, lng: marker.lng }} 
            title={marker.marca} // Mostrar el nombre de la marca en el tooltip del marcador
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
