import React, { useEffect } from 'react'; 
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import UpdatePerson from './components/UpdatePerson';
import Register from './components/Register';
import MapComponent from './components/MapComponent'; // Importa el componente de mapa
import fetchCoordinates from './services/PersonService'; // Asegúrate de que esto esté disponible
// import fetchCoordinates from './api/PersonService'; a

const App = () => {
    useEffect(() => {
        const initMap = () => {
            fetchCoordinates().then(coordinates => {
                if (coordinates) {
                    const labels = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
                    let labelIndex = 0;

                    const bangalore = { lat: 4.4279668, lng: -75.21346609999999 };
                    const map = new window.google.maps.Map(document.getElementById("map"), {
                        zoom: 14,
                        center: bangalore,
                    });

                    for (const coord of coordinates) {
                        addMarker({ lat: coord.longitud, lng: coord.latitud }, map, coord.marca);
                    }

                    function addMarker(location, map, label) {
                        new window.google.maps.Marker({
                            position: location,
                            label: labels[labelIndex++ % labels.length],
                            title: label,
                            map: map,
                        });
                    }
                }
            });
        };

        // Cargar la API de Google Maps
        const script = document.createElement('script');
        script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyD9o2ubO-TwI1lrxBKnZYApnDAbCilCfeU&callback=initMap&libraries=localContext,places&v=weekly";
        script.defer = true;
        window.initMap = initMap; // Asigna la función initMap a la ventana global
        document.head.appendChild(script); // Agrega el script al head del documento

        return () => {
            // Limpiar el script si el componente se desmonta
            document.head.removeChild(script);
        };
    }, []);

    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/home" element={<Home />} />
                <Route path="/update/:id" element={<UpdatePerson />} />
                <Route path="/register" element={<Register />} />
                <Route path="/map" element={<MapComponent />} /> {/* Ruta para el MapComponent */}
            </Routes>
        </Router>
    );
};

export default App;
