/* global google */
import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 4.6109, // Coordenadas de ejemplo para Ibagué, Tolima
  lng: -75.5000,
};

const MapComponent = () => {
  const [mapLoaded, setMapLoaded] = useState(false);

  useEffect(() => {
    const initializeMap = () => {
      setMapLoaded(true);
    };

    // El callback se ejecutará una vez que la API de Google Maps haya cargado
    window.initializeMap = initializeMap;

    // Cargar la API de Google Maps de manera asíncrona
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc&callback=initializeMap`;
    script.async = true;
    script.defer = true;
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
      delete window.initializeMap;
    };
  }, []);

  return (
    <LoadScript googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc">
      {mapLoaded && (
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={center}
          zoom={10}
        >
          <Marker position={center} />
        </GoogleMap>
      )}
    </LoadScript>
  );
};

export default MapComponent;
