import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './components/Login.css'; // Asegúrate de tener este archivo CSS para efectos adicionales

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [apiKey, setApiKey] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchApiKey();
    }, [username]);

    const fetchApiKey = async () => {
        if (username) {
            try {
                const response = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        username: username
                    }
                });
                setApiKey(response.data.apiKey);
                setError('');
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
                setError('No se pudo recuperar la API Key');
            }
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!apiKey) {
            setError('No se pudo recuperar la API Key');
            return;
        }

        try {
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: {
                    apikey: apiKey
                }
            });
            console.log(response.data);
            // Guardar el token en el localStorage o en el estado global
            localStorage.setItem('token', response.data.token);
            navigate('/home');
        } catch (error) {
            setError('Credenciales inválidas');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container mt-5">
            <div className="card shadow-lg" style={{ maxWidth: '400px', margin: 'auto' }}>
                <div className="card-header text-center">
                    <h2>Iniciar Sesión</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <input
                                type="text"
                                className="form-control"
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contraseña</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit" className="btn btn-primary mt-3 w-100 login-btn">Iniciar Sesión</button>
                        <button 
                            type="button" 
                            className="btn btn-link mt-3 w-100" 
                            onClick={() => navigate('/register')}
                        >
                            Registrarse
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
