import React, { useEffect, useState } from 'react';

const USUARIO_BASE_REST_API_URL = "http://localhost:9001/Laboratorio1/personas"; // URL para obtener personas
const AUTH_URL = "http://localhost:9001/authenticate"; // URL para la autenticación

const Home = () => {
    const [personas, setPersonas] = useState([]);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        // Intenta cargar la lista de personas al montar el componente
        loadPersonas();
    }, []);

    const loadPersonas = async () => {
        const token = localStorage.getItem('token'); // Obtén el token del almacenamiento local
        try {
            const response = await fetch(USUARIO_BASE_REST_API_URL, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });
            if (!response.ok) {
                throw new Error('Error al cargar personas');
            }
            const data = await response.json();
            setPersonas(data);
        } catch (error) {
            console.error("Error loading personas:", error);
        }
    };

    const handleLogin = async () => {
        try {
            const response = await fetch(AUTH_URL, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'apikey': apiKey
                },
                body: JSON.stringify({ username, password })
            });

            if (!response.ok) {
                throw new Error('Error de autenticación');
            }

            const data = await response.json();
            localStorage.setItem('token', data.token); // Guarda el token en localStorage
            loadPersonas(); // Carga la lista de personas después de autenticar
        } catch (error) {
            setError("Error de autenticación. Verifica tus credenciales.");
            console.error("Error de autenticación:", error);
        }
    };

    return (
        <div>
            <h1>Bienvenido a la aplicación</h1>
            <div>
                <h2>Iniciar Sesión</h2>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="API Key"
                    value={apiKey}
                    onChange={(e) => setApiKey(e.target.value)}
                />
                <button onClick={handleLogin}>Iniciar Sesión</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </div>

            <h2>Lista de Personas</h2>
            {personas.length > 0 ? (
                <ul>
                    {personas.map((persona) => (
                        <li key={persona.id}>{persona.nombre}</li> // Cambia 'id' y 'nombre' según tu estructura
                    ))}
                </ul>
            ) : (
                <p>No hay personas disponibles.</p>
            )}
        </div>
    );
};

export default Home;
