// Home.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';

const Home = () => {
    const [personas, setPersonas] = useState([]);

    // Fetch personas al montar el componente
    useEffect(() => {
        const fetchPersonas = async () => {
            const response = await fetch('http://localhost:9001/Laboratorio1/personas');
            const data = await response.json();
            setPersonas(data);
        };
        fetchPersonas();
    }, []);

    const handleDelete = async (id) => {
        const result = await Swal.fire({
            title: '¿Estás seguro?',
            text: "¡No podrás revertir esto!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Sí, eliminarlo!'
        });

        if (result.isConfirmed) {
            const response = await fetch(`http://localhost:9001/Laboratorio1/persona/${id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                setPersonas(personas.filter(persona => persona.id !== id));
                Swal.fire(
                    '¡Eliminado!',
                    'La persona ha sido eliminada.',
                    'success'
                );
            } else {
                Swal.fire(
                    'Error',
                    'No se pudo eliminar la persona.',
                    'error'
                );
            }
        }
    };

    return (
        <div className="container mt-5">
            <h2>Lista de Personas</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Email</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {personas.map(persona => (
                        <tr key={persona.id}>
                            <td>{persona.id}</td>
                            <td>{persona.nombre}</td>
                            <td>{persona.email}</td>
                            <td>
                                <Link to={`/edit/${persona.id}`} className="btn btn-primary">Editar</Link>
                                <button className="btn btn-danger" onClick={() => handleDelete(persona.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Home;
