import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { GoogleMap, useLoadScript, MarkerF, InfoWindowF } from '@react-google-maps/api';
import { Link } from 'react-router-dom';
import Select from 'react-select';
import PersonaService from '../services/PersonaService';
import CoordenadasService from '../services/CoordenadasService';

const mapContainerStyle = {
  height: '400px',
  width: '100%',
};

const defaultCenter = { lat: 4.570868, lng: -74.297333 }; // Default center of Colombia
const libraries = ['places'];

function MapaMultiple() {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const [markers, setMarkers] = useState([]);
  const [activeMarker, setActiveMarker] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc",
    libraries,
  });

  useEffect(() => {
    const fetchPersonas = async () => {
      try {
        const data = await PersonaService.getAllPersons();
        setPersonas(data);
      } catch (error) {
        console.error('Error fetching personas:', error);
        setError('Error al cargar la lista de personas');
      }
    };

    fetchPersonas();
  }, []);

  useEffect(() => {
    const fetchCoordenadas = async () => {
      setIsLoading(true);
      try {
        if (selectedPersonas.length > 0) {
          const ids = selectedPersonas.map(persona => persona.value);
          const coordenadas = await CoordenadasService.getCoordenadasPorIds(ids);
          const markersData = coordenadas.map(coord => ({
            id: coord.id,
            position: { lat: parseFloat(coord.longitud), lng: parseFloat(coord.latitud) },
            title: coord.marca,
          }));
          setMarkers(markersData);
        } else {
          setMarkers([]);
        }
      } catch (error) {
        console.error('Error fetching coordinates:', error);
        setError('Error al cargar las coordenadas');
      } finally {
        setIsLoading(false);
      }
    };

    fetchCoordenadas();
  }, [selectedPersonas]);

  const handlePersonaChange = (selectedOptions) => {
    setSelectedPersonas(selectedOptions);
  };

  const handleMarkerClick = useCallback((marker) => {
    setActiveMarker(marker);
  }, []);

  const handleInfoWindowClose = useCallback(() => {
    setActiveMarker(null);
  }, []);

  const mapOptions = useMemo(() => ({
    disableDefaultUI: true,
    clickableIcons: false,
    scrollwheel: true,
  }), []);

  const renderMap = useMemo(() => {
    if (!isLoaded) return null;

    return (
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={markers.length > 0 ? markers[0].position : defaultCenter}
        zoom={markers.length > 0 ? 10 : 5}
        options={mapOptions}
      >
        {markers.map((marker) => (
          <MarkerF
            key={marker.id}
            position={marker.position}
            onClick={() => handleMarkerClick(marker)}
          >
            {activeMarker === marker && (
              <InfoWindowF
                position={marker.position}
                onCloseClick={handleInfoWindowClose}
              >
                <div>
                  <h3 className="font-bold">{marker.title}</h3>
                  <p>ID: {marker.id}</p>
                  <p>Latitud: {marker.position.lat.toFixed(6)}</p>
                  <p>Longitud: {marker.position.lng.toFixed(6)}</p>
                </div>
              </InfoWindowF>
            )}
          </MarkerF>
        ))}
      </GoogleMap>
    );
  }, [isLoaded, markers, activeMarker, handleMarkerClick, handleInfoWindowClose, mapOptions]);

  if (loadError) {
    return <div>Error al cargar el mapa</div>;
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-b from-blue-500 to-blue-700 p-4">
      <div className="w-full max-w-3xl bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-2xl font-bold mb-4 text-gray-800">Mapa Múltiple</h1>
        
        <Select
          isMulti
          options={personas.map(persona => ({ value: persona.idpersona, label: persona.nombre }))}
          onChange={handlePersonaChange}
          placeholder="Seleccionar personas"
          className="mb-4"
        />
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
            <strong className="font-bold">Error!</strong>
            <span className="block sm:inline"> {error}</span>
          </div>
        )}
        
        {isLoading ? (
          <div className="flex justify-center items-center h-[400px]">
            <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          renderMap
        )}

        <div className="mt-4 flex justify-center">
          <Link to="/home" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Volver al Inicio
          </Link>
        </div>
      </div>
    </div>
  );
}

export default MapaMultiple;
