import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'primereact/button'; // Importar el componente Button de PrimeReact

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [apiKey, setApiKey] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchApiKey(); 
    }, [username]);

    const fetchApiKey = async () => {
        if (username) {
            try {
                const response = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        username: username
                    }
                });
                setApiKey(response.data.apiKey);
                setError(''); 
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
                setError('No se pudo recuperar la API Key');
            }
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        if (!apiKey) {
            setError('No se pudo recuperar la API Key');
            return;
        }

        try {
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: {
                    apikey: apiKey
                }
            });
            console.log(response.data);
            localStorage.setItem('token', response.data.token);
            navigate('/home');
        } catch (error) {
            setError('Credenciales inv치lidas');
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container-fluid vh-100 d-flex align-items-center justify-content-center" style={{ backgroundColor: '#4a90e2' }}>
            <div className="card" style={{ maxWidth: '400px', width: '100%' }}>
                <div className="card-header text-center">
                    <h2>Iniciar Sesi칩n</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <input
                                type="text"
                                className="form-control"
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contrase침a</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="Ingresa tu contrase침a"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <Button 
                            type="submit" 
                            label="Iniciar Sesi칩n" 
                            className="mt-3 p-button-info" // Cambia el color del bot칩n a azul
                        />
                        <button 
                            type="button" 
                            className="btn btn-link mt-3" 
                            onClick={() => navigate('/register')}
                        >
                            Registrarse
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
