import { useEffect, useRef } from 'react';
import PersonaService from './PersonaService';

const MapComponent = () => {
  const mapRef = useRef(null);

  useEffect(() => {
    const initMap = async () => {
      try {
        // Cargar coordenadas desde tu servicio
        const coordinates = await PersonaService.fetchCoordinates([/* IDs aquí */]);
        const map = new window.google.maps.Map(mapRef.current, {
          center: { lat: coordinates[0].lat, lng: coordinates[0].lng },
          zoom: 10,
        });

        // Añadir marcadores
        coordinates.forEach(coord => {
          new window.google.maps.Marker({
            position: { lat: coord.lat, lng: coord.lng },
            map: map,
          });
        });
      } catch (error) {
        console.error('Error al inicializar el mapa:', error);
      }
    };

    initMap();
  }, []);

  return <div id="map" ref={mapRef} style={{ height: '600px', width: '100%' }} />;
};

export default MapComponent;
