import React, { useEffect, useState } from 'react';
import { Wrapper, Status } from "@googlemaps/react-wrapper";
import { AdvancedMarkerElement } from '@googlemaps/marker'; // Asegúrate de que esta importación sea correcta

const render = (status) => {
    if (status === Status.LOADING) return <h1>Loading...</h1>;
    if (status === Status.FAILURE) return <h1>Error loading maps</h1>;
    return null;
};

const MapComponent = () => {
    const [map, setMap] = useState(null);
    const [markers, setMarkers] = useState([]);
    const [coordinates, setCoordinates] = useState([]);

    useEffect(() => {
        const fetchCoordinates = async () => {
            try {
                const response = await fetch('YOUR_API_ENDPOINT'); // Cambia esto a tu endpoint
                const data = await response.json();
                setCoordinates(data.coordinates); // Asume que el API devuelve un arreglo de coordenadas
            } catch (error) {
                console.error('Error fetching coordinates:', error);
            }
        };

        fetchCoordinates();
    }, []);

    useEffect(() => {
        if (map) {
            markers.forEach(marker => marker.setMap(null)); // Limpiar marcadores anteriores
            const newMarkers = coordinates.map(coord => {
                const marker = new AdvancedMarkerElement({
                    map: map,
                    position: { lat: coord.lat, lng: coord.lng },
                    title: coord.title, // Si tus datos tienen un título
                });
                return marker;
            });
            setMarkers(newMarkers);
        }
    }, [map, coordinates]);

    return (
        <div>
            <Wrapper apiKey="YOUR_GOOGLE_MAPS_API_KEY" render={render}>
                <div
                    id="map"
                    style={{ width: "100%", height: "500px" }}
                    ref={(ref) => {
                        if (ref && !map) {
                            const googleMap = new window.google.maps.Map(ref, {
                                center: { lat: 0, lng: 0 }, // Ajusta según sea necesario
                                zoom: 2, // Ajusta según sea necesario
                                disableDefaultUI: true,
                                scrollwheel: true,
                            });

                            googleMap.addListener('touchstart', () => {
                                console.log('Touch start');
                            }, { passive: true });

                            googleMap.addListener('touchmove', () => {
                                console.log('Touch move');
                            }, { passive: true });

                            setMap(googleMap);
                        }
                    }}
                />
            </Wrapper>
        </div>
    );
};

export default MapComponent;
