import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

// Componente del mapa
const MapComponent = ({ coordinates }) => {
  useEffect(() => {
    // Aquí se puede inicializar el mapa
    const initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        zoom: 8,
        center: coordinates.length > 0 ? coordinates[0] : { lat: 0, lng: 0 },
      });

      // Agregar marcadores para cada coordenada
      coordinates.forEach((coord) => {
        new window.google.maps.Marker({
          position: coord,
          map: map,
          title: coord.nombre, // Título del marcador
        });
      });
    };

    if (window.google) {
      initMap();
    }
  }, [coordinates]);

  return <div id="map" style={{ height: '400px', width: '100%' }}></div>;
};

// Componente principal
const UserMapComponent = () => {
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:9001/Laboratorio1/usuario');
        setUsers(response.data);
      } catch (error) {
        console.error('Error al obtener usuarios:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleUserSelect = (userId) => {
    setSelectedUsers((prevSelected) =>
      prevSelected.includes(userId)
        ? prevSelected.filter((id) => id !== userId)
        : [...prevSelected, userId]
    );
  };

  const handleConsultLocation = () => {
    const userLocations = users.filter(user => selectedUsers.includes(user.id)).map(user => ({
      lat: user.latitud, // Asegúrate de que estas propiedades existan
      lng: user.longitud,
      nombre: user.nombre,
    }));
    console.log('Coordenadas de usuarios seleccionados:', userLocations);
  };

  // Obtener las coordenadas de los usuarios seleccionados
  const userLocations = users
    .filter(user => selectedUsers.includes(user.id))
    .map(user => ({
      lat: user.latitud, // Asegúrate de que estas propiedades existan
      lng: user.longitud,
      nombre: user.nombre,
    }));

  return (
    <div>
      <h1>Lista de Usuarios</h1>
      <button onClick={() => history.push('/')}>Volver al Home</button>
      <div>
        {users.map(user => (
          <div key={user.id}>
            <input
              type="checkbox"
              checked={selectedUsers.includes(user.id)}
              onChange={() => handleUserSelect(user.id)}
            />
            {user.nombre} {/* Ajusta esto según la propiedad que contenga el nombre del usuario */}
          </div>
        ))}
      </div>
      <button onClick={handleConsultLocation} disabled={selectedUsers.length === 0}>
        Consultar Ubicación
      </button>

      {/* Mostrar el mapa solo si hay ubicaciones seleccionadas */}
      {userLocations.length > 0 && (
        <MapComponent coordinates={userLocations} />
      )}
    </div>
  );
};

export default UserMapComponent;
