/* global google */
import React, { useEffect, useRef } from 'react';

const MapComponent = () => {
  const mapRef = useRef(null); // Referencia para el contenedor del mapa

  useEffect(() => {
    loadGoogleMaps();
  }, []);

  const loadGoogleMaps = () => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&libraries=marker`;
    script.async = true;
    script.defer = true;
    script.onload = initializeMap; // Llama a initializeMap después de que el script se haya cargado
    document.body.appendChild(script);
  };

  const initializeMap = () => {
    const map = new google.maps.Map(mapRef.current, {
      center: { lat: -34.397, lng: 150.644 },
      zoom: 8,
    });

    // Ejemplo de agregar un marcador
    const marker = new google.maps.Marker({
      position: { lat: -34.397, lng: 150.644 },
      map: map,
      title: '¡Hola mundo!',
    });
  };

  return (
    <div>
      <h1>Mi Mapa</h1>
      <div ref={mapRef} style={{ height: '500px', width: '100%' }}></div>
    </div>
  );
};

export default MapComponent;
