import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import PersonaService from '../services/PersonaService';

const MapComponent = () => {
  const [personas, setPersonas] = useState([]);
  const [selectedPersonas, setSelectedPersonas] = useState([]);
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPersonas = async () => {
      try {
        const data = await PersonaService.getAllPersons();
        if (data) {
          setPersonas(data);
        }
      } catch (error) {
        console.error('Error al obtener personas:', error);
      }
    };

    fetchPersonas();
  }, []);

  const initMap = useCallback(() => {
    if (window.google && window.google.maps) {
      const mapInstance = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: 4.43889, lng: -75.23222 },
        zoom: 10,
      });
      setMap(mapInstance);
    } else {
      console.error('Google Maps no est치 cargado correctamente');
    }
  }, []);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyBtmSbtAkZMgopeMNzXW4NC4LyzXRLr-ck&callback=initMap`;
    script.async = true;
    script.defer = true;
    document.head.appendChild(script);

    window.initMap = initMap;

    return () => {
      document.head.removeChild(script);
    };
  }, [initMap]);

  useEffect(() => {
    if (map && selectedPersonas.length > 0) {
      markers.forEach(marker => marker.setMap(null));
      
      PersonaService.fetchCoordinates(selectedPersonas.map(p => p.id))
        .then(coordinates => {
          const newMarkers = coordinates.map(coord => {
            const lat = parseFloat(coord.latitud);
            const lng = parseFloat(coord.longitud);
            
            if (isNaN(lat) || isNaN(lng)) {
              console.error('Coordenadas inv치lidas:', coord);
              return null;
            }

            const marker = new window.google.maps.Marker({
              position: { lat, lng },
              map: map,
              title: coord.nombre || 'Desconocido',
              animation: window.google.maps.Animation.DROP
            });

            const infoWindow = new window.google.maps.InfoWindow({
              content: `<div><strong>${coord.nombre}</strong><br>Lat: ${lat}<br>Lng: ${lng}</div>`
            });

            marker.addListener('click', () => {
              infoWindow.open(map, marker);
            });

            return marker;
          }).filter(marker => marker !== null);

          setMarkers(newMarkers);

          if (newMarkers.length > 0) {
            const bounds = new window.google.maps.LatLngBounds();
            newMarkers.forEach(marker => bounds.extend(marker.getPosition()));
            map.fitBounds(bounds);
          }
        })
        .catch(error => console.error('Error al obtener coordenadas:', error));
    }
  }, [map, selectedPersonas]);

  const handleSelectChange = (e) => {
    const selectedIds = Array.from(e.target.selectedOptions, option => option.value);
    const selectedPersons = personas.filter(persona => selectedIds.includes(persona.id.toString()));
    setSelectedPersonas(selectedPersons);
  };

  const handleSelectAll = () => {
    setSelectedPersonas(personas);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredPersonas = personas.filter(persona => 
    `${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`
      .toLowerCase()
      .includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto p-4 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Mapa de Ubicaciones</h1>
      <div className="flex flex-col md:flex-row gap-6">
        <div className="w-full md:w-2/3">
          <div id="map" className="h-[500px] w-full rounded-lg shadow-lg"></div>
        </div>
        <div className="w-full md:w-1/3 space-y-4">
          <input
            type="text"
            className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            placeholder="Buscar por nombre completo"
            value={searchTerm}
            onChange={handleSearch}
          />
          <div>
            <label htmlFor="personasSelect" className="block text-sm font-medium text-gray-700 mb-1">
              Seleccione personas:
            </label>
            <select 
              id="personasSelect" 
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              multiple 
              onChange={handleSelectChange}
              size="10"
              value={selectedPersonas.map(p => p.id.toString())}
            >
              {filteredPersonas.map(persona => (
                <option key={persona.id} value={persona.id} className="py-1 px-2 hover:bg-gray-100">
                  {`${persona.primerNombre} ${persona.segundoNombre} ${persona.primerApellido} ${persona.segundoApellido}`}
                </option>
              ))}
            </select>
          </div>
          <div className="flex gap-4">
            <button 
              className="flex-1 bg-gray-500 hover:bg-gray-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:scale-105"
              onClick={handleSelectAll}
            >
              Seleccionar Todos
            </button>
            <button 
              className="flex-1 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300 ease-in-out transform hover:scale-105"
              onClick={() => navigate('/home')}
            >
              Volver a Home
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MapComponent;
