import axios from 'axios';

const API_URL = 'http://localhost:9001'; // Cambia esto según tu configuración de backend

// Servicio de autenticación
const AuthService = {
  // Método para autenticar al usuario y obtener el token
  authenticate: async (username, password, apiKey) => {
    try {
      const response = await axios.post(`${API_URL}/authenticate`, {
        username,
        password,
      }, {
        headers: {
          apikey: apiKey, // Enviamos la API key en los encabezados
        },
      });

      // Devuelve el token y la API key
      return response.data; // Debería contener { token, apikey }
    } catch (error) {
      console.error('Error en la autenticación', error);
      throw error; // Propaga el error para manejarlo en el componente
    }
  },
};

export default AuthService;
