import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Register = () => {
  const [formData, setFormData] = useState({
    pnombre: "",
    ubicacion: "",
    identificacion: "",
    email: "",
    fechaNacimiento: "",
    snombre: "",
    papellido: "",
    sapellido: "",
    usuario: {
      login: "",
      password: "",
    },
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState(""); // Tipo de modal ("success" o "error")

  // Función para manejar los cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("usuario.")) {
      const usuarioField = name.split(".")[1];
      setFormData((prevData) => ({
        ...prevData,
        usuario: { ...prevData.usuario, [usuarioField]: value },
      }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  // Función para manejar el envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();

    // Validar la fecha de nacimiento
    const currentDate = new Date();
    const selectedDate = new Date(formData.fechaNacimiento);
    const year = selectedDate.getFullYear();

    if (selectedDate > currentDate) {
      alert("La fecha de nacimiento no puede ser futura.");
      return;
    }

    if (year < 1900 || year > 2100) {
      alert("El año de nacimiento debe estar entre 1900 y 2100.");
      return;
    }

    // Preparar los datos para enviar
    const dataToSend = {
      pnombre: formData.pnombre,
      ubicacion: formData.ubicacion,
      identificacion: parseInt(formData.identificacion),
      email: formData.email,
      fechaNacimiento: formData.fechaNacimiento,
      snombre: formData.snombre,
      papellido: formData.papellido,
      sapellido: formData.sapellido,
      usuario: {
        login: formData.usuario.login,
        password: formData.usuario.password,
      },
    };

    // Enviar la solicitud POST al backend
    fetch("http://localhost:9001/Laboratorio1/persona", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          return response.json().then((err) => {
            throw new Error(err.message || "Error en la solicitud.");
          });
        }
      })
      .then((data) => {
        // Verifica si el mensaje indica un registro exitoso
        if (data.message === "Persona registrada exitosamente.") {
          setModalMessage(data.message);
          setModalType("success"); // Establecer tipo de modal como "success"
        } else {
          // Para mensajes de error (ej., duplicados)
          setModalMessage(data.message || "Error en el registro.");
          setModalType("error"); // Establecer tipo de modal como "error"
        }
        setShowModal(true);
      })
      .catch((error) => {
        console.error("Error:", error);
        setModalMessage(error.message); // Mostrar mensaje de error en el modal
        setModalType("error"); // Establecer tipo de modal como "error"
        setShowModal(true);
      });
  };

  // Función para cerrar el modal y resetear el formulario
  const handleCloseModal = () => {
    setShowModal(false);
    // No resetear el formulario para mantener los datos ingresados
  };

  return (
    <div className="container mt-5">
      <div className="card">
        <div className="card-header">
          <h2>Agregar Persona</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            {/* Formulario de registro */}
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Nombre:</label>
                <input
                  type="text"
                  name="pnombre"
                  className="form-control"
                  value={formData.pnombre}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Nombre:</label>
                <input
                  type="text"
                  name="snombre"
                  className="form-control"
                  value={formData.snombre}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Apellido:</label>
                <input
                  type="text"
                  name="papellido"
                  className="form-control"
                  value={formData.papellido}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Apellido:</label>
                <input
                  type="text"
                  name="sapellido"
                  className="form-control"
                  value={formData.sapellido}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Ubicación:</label>
                <input
                  type="text"
                  name="ubicacion"
                  className="form-control"
                  value={formData.ubicacion}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Identificación:</label>
                <input
                  type="number"
                  name="identificacion"
                  className="form-control"
                  value={formData.identificacion}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Fecha de Nacimiento:</label>
                <input
                  type="date"
                  name="fechaNacimiento"
                  className="form-control"
                  value={formData.fechaNacimiento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3>Usuario</h3>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Login:</label>
                <input
                  type="text"
                  name="usuario.login"
                  className="form-control"
                  value={formData.usuario.login}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Password:</label>
                <input
                  type="password"
                  name="usuario.password"
                  className="form-control"
                  value={formData.usuario.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              Guardar
            </button>
            <button
              type="button"
              className="btn btn-secondary ms-3"
              onClick={() => (window.location.href = "/login")}
            >
              Regresar a Login
            </button>
          </form>
        </div>
      </div>

      {/* Modal para mostrar mensajes */}
      {showModal && (
        <div className={`modal fade show`} style={{ display: "block" }} tabIndex="-1" role="dialog">
          <div className="modal-dialog" role="document">
            <div className={`modal-content ${modalType === "success" ? "bg-success text-white" : "bg-danger text-white"}`}>
              <div className="modal-header">
                <h5 className="modal-title">
                  {modalType === "success" ? "Éxito" : "Error"}
                </h5>
                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
              </div>
              <div className="modal-body">
                <p>{modalMessage}</p>
                </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                  Cerrar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Register;
