import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import MapComponent from './MapComponent'; // Asegúrate de importar tu componente de mapa

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const history = useHistory();

  useEffect(() => {
    // Función para obtener usuarios desde el backend
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:9001/Laboratorio1/usuario');
        setUsers(response.data);
      } catch (error) {
        console.error('Error al obtener usuarios:', error);
      }
    };

    fetchUsers();
  }, []);

  // Función para manejar la selección de usuarios
  const handleUserSelect = (userId) => {
    setSelectedUsers((prevSelected) =>
      prevSelected.includes(userId)
        ? prevSelected.filter((id) => id !== userId)
        : [...prevSelected, userId]
    );
  };

  // Manejar la consulta de ubicación
  const handleConsultLocation = () => {
    // Aquí puedes obtener las coordenadas de los usuarios seleccionados
    const userLocations = users.filter(user => selectedUsers.includes(user.id));
    // Lógica para mostrar el mapa con las ubicaciones de los usuarios
    // Por ejemplo, podrías tener un estado para almacenar las coordenadas
    console.log('Coordenadas de usuarios seleccionados:', userLocations);
  };

  return (
    <div>
      <h1>Lista de Usuarios</h1>
      <button onClick={() => history.push('/')}>Volver al Home</button>
      <div>
        {users.map(user => (
          <div key={user.id}>
            <input
              type="checkbox"
              checked={selectedUsers.includes(user.id)}
              onChange={() => handleUserSelect(user.id)}
            />
            {user.nombre} {/* Ajusta esto según la propiedad que contenga el nombre del usuario */}
          </div>
        ))}
      </div>
      <button onClick={handleConsultLocation} disabled={selectedUsers.length === 0}>
        Consultar Ubicación
      </button>

      {/* Aquí puedes incluir el componente del mapa con las ubicaciones seleccionadas */}
      {selectedUsers.length > 0 && (
        <MapComponent coordinates={userLocations} />
      )}
    </div>
  );
};

export default UserList;
