import React, { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom'; // Importa useNavigate para la navegación

const MapComponent = () => {
  const navigate = useNavigate(); // Inicializa el hook para la navegación

  useEffect(() => {
    const initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: -34.397, lng: 150.644 }, // Coordenadas iniciales del mapa
        zoom: 8, // Nivel de zoom inicial
      });
    };

    // Cargar la API de Google Maps
    const script = document.createElement('script');
    script.src = "https://maps.googleapis.com/maps/api/js?key=AIzaSyD9o2ubO-TwI1lrxBKnZYApnDAbCilCfeU&callback=initMap&libraries=localContext,places&v=weekly";
    script.defer = true;
    window.initMap = initMap; // Asigna la función initMap a la ventana global
    document.head.appendChild(script); // Agrega el script al head del documento

    return () => {
      // Limpiar el script si el componente se desmonta
      document.head.removeChild(script);
    };
  }, []);

  return (
    <div className="container-fluid h-100 d-flex flex-column">
      <div className="row flex-grow-1">
        <div id="map" className="col-12 h-100"></div> {/* Aquí se renderizará el mapa */}
      </div>
      <button 
        className="btn btn-primary mt-3" 
        onClick={() => navigate('/home')} // Navega al Home
      >
        Volver a Home
      </button>
    </div>
  );
};

export default MapComponent;
