import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const Register = () => {
  const [formData, setFormData] = useState({
    pnombre: "",
    ubicacion: "",
    identificacion: "",
    email: "",
    fechaNacimiento: "",
    snombre: "",
    papellido: "",
    sapellido: "",
    usuario: {
      login: "",
      password: "",
    },
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("usuario.")) {
      const usuarioField = name.split(".")[1];
      setFormData((prevData) => ({
        ...prevData,
        usuario: { ...prevData.usuario, [usuarioField]: value },
      }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const currentDate = new Date();
    const selectedDate = new Date(formData.fechaNacimiento);
    const year = selectedDate.getFullYear();

    if (selectedDate > currentDate) {
      alert("La fecha de nacimiento no puede ser futura.");
      return;
    }

    if (year < 1900 || year > 2100) {
      alert("El año de nacimiento debe estar entre 1900 y 2100.");
      return;
    }

    const dataToSend = {
      ...formData,
      identificacion: parseInt(formData.identificacion),
    };

    try {
      const response = await fetch("http://localhost:9001/Laboratorio1/persona", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dataToSend),
      });

      let responseData;
      const contentType = response.headers.get("content-type");
      if (contentType && contentType.includes("application/json")) {
        responseData = await response.json();
      } else {
        responseData = await response.text();
      }

      if (response.ok) {
        setModalMessage(typeof responseData === 'object' ? responseData.message : responseData);
        setModalType("success");
      } else {
        setModalMessage(typeof responseData === 'object' ? responseData.message : responseData);
        setModalType("error");
      }
      setShowModal(true);
    } catch (error) {
      console.error("Error:", error);
      setModalMessage("Error en la solicitud.");
      setModalType("error");
      setShowModal(true);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    if (modalType === "success") {
      setFormData({
        pnombre: "",
        ubicacion: "",
        identificacion: "",
        email: "",
        fechaNacimiento: "",
        snombre: "",
        papellido: "",
        sapellido: "",
        usuario: {
          login: "",
          password: "",
        },
      });
    }
  };

  const handleReturnToLogin = () => {
    // Asumiendo que la ruta de login es "/login"
    window.location.href = "/login";
  };

  return (
    <div className="container mt-5">
      <div className="card">
        <div className="card-header">
          <h2>Agregar Persona</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Nombre:</label>
                <input
                  type="text"
                  name="pnombre"
                  className="form-control"
                  value={formData.pnombre}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Nombre:</label>
                <input
                  type="text"
                  name="snombre"
                  className="form-control"
                  value={formData.snombre}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Apellido:</label>
                <input
                  type="text"
                  name="papellido"
                  className="form-control"
                  value={formData.papellido}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Apellido:</label>
                <input
                  type="text"
                  name="sapellido"
                  className="form-control"
                  value={formData.sapellido}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Ubicación:</label>
                <input
                  type="text"
                  name="ubicacion"
                  className="form-control"
                  value={formData.ubicacion}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Identificación:</label>
                <input
                  type="number"
                  name="identificacion"
                  className="form-control"
                  value={formData.identificacion}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Fecha de Nacimiento:</label>
                <input
                  type="date"
                  name="fechaNacimiento"
                  className="form-control"
                  value={formData.fechaNacimiento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3>Usuario</h3>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Login:</label>
                <input
                  type="text"
                  name="usuario.login"
                  className="form-control"
                  value={formData.usuario.login}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Password:</label>
                <input
                  type="password"
                  name="usuario.password"
                  className="form-control"
                  value={formData.usuario.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Guardar
              </button>
              <button type="button" className="btn btn-secondary" onClick={handleReturnToLogin}>
                Regresar a Login
              </button>
            </div>
          </form>
        </div>
      </div>

      {showModal && (
        <div className="modal" tabIndex="-1" role="dialog" style={{ display: "block" }}>
          <div className="modal-dialog" role="document">
            <div className={`modal-content ${modalType === "success" ? "bg-success" : "bg-danger"}`}>
              <div className="modal-header">
                <h5 className="modal-title text-white">
                  {modalType === "success" ? "Éxito" : "Error"}
                </h5>
                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
              </div>
              <div className="modal-body">
                <p className="text-white">{modalMessage}</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                  Cerrar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Register;
