import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';  // Importa useNavigate
import 'bootstrap/dist/css/bootstrap.min.css';
import PersonaService from '../services/PersonaService';

const Register = () => {
  const navigate = useNavigate();  // Crea la instancia de navigate
  const [formData, setFormData] = useState({
    primerNombre: "",
    segundoNombre: "",
    primerApellido: "",
    segundoApellido: "",
    identificacion: "",
    email: "",
    fechaNacimiento: "",
    ubicacion: {
      lugar: "",
      ciudad: "",
      departamento: "",
    },
    usuario: {
      login: "",
      password: "",
    },
  });

  const [alertInfo, setAlertInfo] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [objectName, field] = name.split(".");
      setFormData((prevData) => ({
        ...prevData,
        [objectName]: {
          ...prevData[objectName],
          [field]: value,
        },
      }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const currentDate = new Date();
    const selectedDate = new Date(formData.fechaNacimiento);
    const year = selectedDate.getFullYear();

    if (selectedDate > currentDate) {
      setAlertInfo({ type: 'danger', message: 'La fecha de nacimiento no puede ser futura.' });
      return;
    }

    if (year < 1900 || year > 2100) {
      setAlertInfo({ type: 'danger', message: 'El año de nacimiento debe estar entre 1900 y 2100.' });
      return;
    }

    const dataToSend = {
      ...formData,
      identificacion: parseInt(formData.identificacion),
    };

    try {
      const response = await fetch("http://localhost:9001/Laboratorio1/persona", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dataToSend),
      });

      let responseData;
      const contentType = response.headers.get("content-type");
      if (contentType && contentType.includes("application/json")) {
        responseData = await response.json();
      } else {
        responseData = await response.text();
      }

      if (response.ok) {
        setModalMessage(typeof responseData === 'object' ? responseData.message : responseData);
        setModalType("success");
        setShowModal(true);
        // Redirect to login page after 2 seconds
        setTimeout(() => {
          navigate("/"); // Ahora navigate está definido
        }, 2000);
      } else {
        setModalMessage(typeof responseData === 'object' ? responseData.message : responseData);
        setModalType("error");
        setShowModal(true);
      }
    } catch (error) {
      console.error("Error:", error);
      setModalMessage("Error en la solicitud.");
      setModalType("error");
      setShowModal(true);
    }
  };

  return (
    <div className="container-fluid py-5" style={{ 
      background: 'linear-gradient(to right, #212531, #c9b1ff)', 
      minHeight: '100vh'
    }}>
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h2 className="mb-0">Registrar Persona</h2>
            </div>
            <div className="card-body">
              {alertInfo && (
                <div className={`alert alert-${alertInfo.type}`} role="alert">
                  {alertInfo.message}
                </div>
              )}
              <form onSubmit={handleSubmit}>
                {/* Tu código de formulario aquí */}
                <button type="submit" className="btn btn-success">Registrar</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
