import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const RegistroForm = () => {
  const [formData, setFormData] = useState({
    pnombre: '',
    sNombre: '',
    identificacion: '',
    ubicacion: '',
    pApellido: '',
    sApellido: '',
    email: '',
    fechaNacimiento: '',
    edad: 0,
    login: '',
    password: ''
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Calcular edad
    if (name === 'fechaNacimiento') {
      const today = new Date();
      const birthDate = new Date(value);
      const age = today.getFullYear() - birthDate.getFullYear();
      const monthDiff = today.getMonth() - birthDate.getMonth();
      if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
        setFormData((prev) => ({ ...prev, edad: age - 1 }));
      } else {
        setFormData((prev) => ({ ...prev, edad: age }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:9001/Laboratorio1/persona', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setModalMessage('¡Registro guardado exitosamente!');
        setShowModal(true);
        setFormData({
          pnombre: '',
          sNombre: '',
          identificacion: '',
          ubicacion: '',
          pApellido: '',
          sApellido: '',
          email: '',
          fechaNacimiento: '',
          edad: 0,
          login: '',
          password: ''
        });
      } else {
        setModalMessage('Error al guardar el registro.');
        setShowModal(true);
      }
    } catch (error) {
      console.error('Error:', error);
      setModalMessage('Error al conectar con el servidor.');
      setShowModal(true);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Registro de Usuario</h2>
      <div className="card p-4">
        <form onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group col-md-6">
              <label>Primer Nombre:</label>
              <input
                type="text"
                className="form-control"
                name="pnombre"
                value={formData.pnombre}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label>Segundo Nombre:</label>
              <input
                type="text"
                className="form-control"
                name="sNombre"
                value={formData.sNombre}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group col-md-6">
              <label>Primer Apellido:</label>
              <input
                type="text"
                className="form-control"
                name="pApellido"
                value={formData.pApellido}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group col-md-6">
              <label>Segundo Apellido:</label>
              <input
                type="text"
                className="form-control"
                name="sApellido"
                value={formData.sApellido}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="form-group">
            <label>Identificación:</label>
            <input
              type="number"
              className="form-control"
              name="identificacion"
              value={formData.identificacion}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Ubicación:</label>
            <input
              type="text"
              className="form-control"
              name="ubicacion"
              value={formData.ubicacion}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              className="form-control"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Fecha de Nacimiento:</label>
            <input
              type="date"
              className="form-control"
              name="fechaNacimiento"
              value={formData.fechaNacimiento}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Login:</label>
            <input
              type="text"
              className="form-control"
              name="login"
              value={formData.login}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              className="form-control"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary btn-block">Registrar</button>
        </form>
      </div>

      {/* Modal para mostrar mensajes */}
      {showModal && (
        <div className="modal show" style={{ display: 'block' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Mensaje</h5>
                <button type="button" className="close" onClick={() => setShowModal(false)}>
                  <span>&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>{modalMessage}</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Cerrar</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Register;
