import React, { useState } from 'react';

function Login() {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            const response = await fetch('http://localhost:9001/authenticate', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'apikey': 'YOUR_API_KEY' // Reemplaza con tu API Key si es necesario
                },
                body: JSON.stringify({ username: login, password }), // Ajusta el nombre del campo a 'username'
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Login exitoso', data);
                localStorage.setItem('token', data.token); // Almacena el token en localStorage
                // Aquí puedes redirigir al usuario o actualizar el estado de autenticación
                // Ejemplo: window.location.href = '/dashboard'; // Redirige a otra página
            } else {
                setError('Credenciales inválidas');
            }
        } catch (error) {
            console.error('Error:', error);
            setError('Error al conectar con el servidor');
        }
    };

    return (
        <div className="container login-container">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card shadow">
                        <div className="card-body">
                            <h1 className="card-title text-center mb-3">Login</h1>
                            {error && <div className="alert alert-danger">{error}</div>}
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="login" className="form-label">Login:</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="login"
                                        value={login}
                                        onChange={(e) => setLogin(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">Contraseña:</label>
                                    <input
                                        type="password"
                                        className="form-control"
                                        id="password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                    />
                                </div>
                                <div className="d-grid gap-2 mb-3">
                                    <button type="submit" className="btn btn-primary">Ingresar</button>
                                </div>
                            </form>
                            <div className="d-flex justify-content-between mt-2">
                                <a href="RecuperarUsuarioContraseña.html" className="text-decoration-none">Recuperar acceso</a>
                                <a href="/registro" className="text-decoration-none">Registrarse</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
