import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';

const UpdatePerson = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    const [persona, setPersona] = useState({
        id: id,
        primerNombre: '',
        segundoNombre: '',
        primerApellido: '',
        segundoApellido: '',
        identificacion: '',
        email: '',
        fechaNacimiento: '',
        edadClinica: '',
        ubicacion: {
            lugar: '',
            ciudad: '',
            departamento: '',
        },
        usuario: {
            login: '',
            password: '',
            apikey: ''
        }
    });

    useEffect(() => {
        const loadPersona = async () => {
            const token = localStorage.getItem('token');
            const response = await fetch(`http://localhost:9001/Laboratorio1/persona/id/${id}`, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
            const data = await response.json();
            setPersona(data);
        };

        loadPersona();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPersona({
            ...persona,
            [name]: value,
        });
    };

    const handleUbicacionChange = (e) => {
        const { name, value } = e.target;
        setPersona({
            ...persona,
            ubicacion: {
                ...persona.ubicacion,
                [name]: value,
            }
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:9001/Laboratorio1/persona`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify(persona),
        });

        if (response.ok) {
            Swal.fire('Actualizado', 'Los datos han sido actualizados correctamente.', 'success');
            navigate('/home'); // Redirigir a la página de inicio
        } else {
            Swal.fire('Error', 'No se pudo actualizar los datos.', 'error');
        }
    };

    return (
        <div className="container mt-5">
            <h2>Actualizar Persona</h2>
            <form onSubmit={handleSubmit}>
                {/* Campos del formulario... */}
                <button type="submit" className="btn btn-primary">Actualizar</button>
                <button type="button" className="btn btn-secondary" onClick={() => navigate('/home')}>Cancelar</button>
            </form>
        </div>
    );
};

export default UpdatePerson;
