import React, { useEffect, useState, useCallback } from 'react';
import { GoogleMap, LoadScript, InfoWindow } from '@react-google-maps/api';
import { useParams, Link } from 'react-router-dom';
import CoordenadasService from '../services/CoordenadasService';

const estiloContenedorMapa = {
  height: '400px',
  width: '100%',
};

const centroPredeterminado = { lat: 4.570868, lng: -74.297333 }; // Centro predeterminado de Colombia

function MapaIndividual() {
  const { idpersona } = useParams();
  const [marcador, setMarcador] = useState(null);
  const [marcadorSeleccionado, setMarcadorSeleccionado] = useState(null);
  const [error, setError] = useState(null);
  const [cargando, setCargando] = useState(true);
  const [mapa, setMapa] = useState(null);

  const obtenerCoordenadas = useCallback(async () => {
    try {
      setCargando(true);
      const coordenadas = await CoordenadasService.getCoordenadasIndividual(idpersona);
      
      if (!coordenadas || !coordenadas.direccion) {
        throw new Error('Se recibieron datos inválidos');
      }

      // Usar el servicio de Geocodificación para obtener las coordenadas
      const geocoder = new window.google.maps.Geocoder();
      geocoder.geocode({ address: coordenadas.direccion }, (results, status) => {
        if (status === 'OK' && results[0]) {
          const location = results[0].geometry.location;
          setMarcador({
            id: coordenadas.id,
            marca: coordenadas.marca,
            lat: location.lat(),
            lng: location.lng(),
          });
        } else {
          throw new Error('No se pudo geocodificar la dirección');
        }
        setError(null);
        setCargando(false);
      });
    } catch (error) {
      console.error('Error al obtener coordenadas:', error);
      setError(error.message || 'Error al obtener las coordenadas');
      setCargando(false);
    }
  }, [idpersona]);

  useEffect(() => {
    obtenerCoordenadas();
  }, [obtenerCoordenadas]);

  const onMapLoad = useCallback((map) => {
    setMapa(map);
  }, []);

  const manejarClicMarcador = (marcador) => {
    setMarcadorSeleccionado(marcador);
  };

  const manejarCierreVentanaInfo = () => {
    setMarcadorSeleccionado(null);
  };

  useEffect(() => {
    if (mapa && marcador) {
      const { AdvancedMarkerElement } = window.google.maps.marker;
      new AdvancedMarkerElement({
        map: mapa,
        position: { lat: marcador.lat, lng: marcador.lng },
        title: marcador.marca,
      });
    }
  }, [mapa, marcador]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-b from-blue-500 to-blue-700 p-4">
      <div className="w-full max-w-3xl bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-2xl font-bold mb-4 text-gray-800">Mapa Individual</h1>
        
        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
            <strong className="font-bold">Error!</strong>
            <span className="block sm:inline"> {error}</span>
          </div>
        )}
        
        {cargando ? (
          <div className="flex justify-center items-center h-[400px]">
            <div className="animate-spin rounded-full h-32 w-32 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <LoadScript 
            googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc"
            libraries={['places']}
          >
            <GoogleMap
              mapContainerStyle={estiloContenedorMapa}
              center={marcador ? { lat: marcador.lat, lng: marcador.lng } : centroPredeterminado}
              zoom={marcador ? 12 : 5}
              onLoad={onMapLoad}
            >
              {marcadorSeleccionado && (
                <InfoWindow
                  position={{ lat: marcadorSeleccionado.lat, lng: marcadorSeleccionado.lng }}
                  onCloseClick={manejarCierreVentanaInfo}
                >
                  <div>
                    <h3 className="font-bold">{marcadorSeleccionado.marca}</h3>
                    <p>ID: {marcadorSeleccionado.id}</p>
                    <p>Latitud: {marcadorSeleccionado.lat.toFixed(6)}</p>
                    <p>Longitud: {marcadorSeleccionado.lng.toFixed(6)}</p>
                  </div>
                </InfoWindow>
              )}
            </GoogleMap>
          </LoadScript>
        )}

        <div className="mt-4 flex justify-center">
          <Link to="/home" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Volver al Inicio
          </Link>
        </div>
      </div>
    </div>
  );
}

export default MapaIndividual;
