import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Register = () => {
  const { updateAuthData } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [apiKey, setApiKey] = useState('');
  const [error, setError] = useState('');

  // Función para manejar el registro
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      // Intenta registrar al usuario
      const response = await axios.post('http://localhost:9001/Laboratorio1/persona', {
        username,
        password,
        apiKey,
      });

      // Actualiza el contexto de autenticación
      updateAuthData({ username, password, apiKey });
      alert('Usuario registrado exitosamente!');
      // Aquí puedes redirigir al usuario o limpiar el formulario
    } catch (err) {
      console.error('Error registrando usuario:', err);
      setError('Error al registrar usuario. Verifica la información e inténtalo de nuevo.');
    }
  };

  return (
    <div>
      <h2>Registrar Usuario</h2>
      <form onSubmit={handleRegister}>
        <div>
          <label htmlFor="username">Nombre de Usuario:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Contraseña:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="apiKey">API Key:</label>
    
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
};

export default Register;
