import React, { useEffect, useState, useCallback } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import { useParams, Link } from 'react-router-dom'; 
import CoordenadasService from '../services/CoordenadasService';

const MapaIndividual = () => {
    const { idpersona } = useParams();
    const [marker, setMarker] = useState(null);
    const [selectedMarker, setSelectedMarker] = useState(null);
    const [error, setError] = useState(null);

    const fetchCoordenadas = useCallback(async () => {
        try {
            const coordenadas = await CoordenadasService.getCoordenadasIndividual(idpersona);
            console.log('Coordenadas recibidas:', coordenadas);

            if (!coordenadas || 
                isNaN(parseFloat(coordenadas.latitud)) || 
                isNaN(parseFloat(coordenadas.longitud)) ||
                parseFloat(coordenadas.latitud) < -90 || 
                parseFloat(coordenadas.latitud) > 90 ||
                parseFloat(coordenadas.longitud) < -180 || 
                parseFloat(coordenadas.longitud) > 180) {
                console.error('Coordenadas inv치lidas recibidas:', coordenadas);
                setError('Se recibieron coordenadas inv치lidas');
                return;
            }

            const lat = parseFloat(coordenadas.latitud);
            const lng = parseFloat(coordenadas.longitud);

            console.log('Coordenadas procesadas:', lat, lng);

            const markerData = {
                id: coordenadas.idpersona,
                marca: coordenadas.marca,
                lat: lat,
                lng: lng,
            };

            setMarker(markerData);
            setError(null);
        } catch (error) {
            console.error('Error al obtener coordenadas:', error);
            setError('Error al obtener las coordenadas');
        }
    }, [idpersona]);

    useEffect(() => {
        fetchCoordenadas();
    }, [fetchCoordenadas]);

    const center = marker 
        ? { lat: marker.lat, lng: marker.lng } 
        : { lat: 4.570868, lng: -74.297333 }; // Centro predeterminado de Colombia

    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100vh',
            background: 'linear-gradient(to bottom, #6a11cb, #2575fc)'
        }}>
            {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
            
            <LoadScript googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc">
                <GoogleMap
                    mapContainerStyle={{ height: '400px', width: '800px', borderRadius: '10px' }}
                    center={center}
                    zoom={marker ? 12 : 5}
                >
                    {marker && (
                        <Marker
                            key={marker.id}
                            position={{ lat: marker.lat, lng: marker.lng }}
                            title={marker.marca}
                            onClick={() => setSelectedMarker(marker)}
                        />
                    )}

                    {selectedMarker && (
                        <InfoWindow
                            position={{ lat: selectedMarker.lat, lng: selectedMarker.lng }}
                            onCloseClick={() => setSelectedMarker(null)}
                        >
                            <div>
                                <h3>{selectedMarker.marca}</h3>
                                <p>ID: {selectedMarker.id}</p>
                                <p>Latitud: {selectedMarker.lat.toFixed(6)}</p>
                                <p>Longitud: {selectedMarker.lng.toFixed(6)}</p>
                            </div>
                        </InfoWindow>
                    )}
                </GoogleMap>
            </LoadScript>

            <div className="d-flex justify-content-center mt-4">
                <Link to="/home" className="btn btn-info">Volver Home</Link>
            </div>
        </div>
    );
};

export default MapaIndividual;
