import axios from "axios";

const USUARIO_BASE_REST_API_URL = "http://localhost:9001/Laboratorio1/personas";

class PersonaService {
    getAll() {
        const token = localStorage.getItem('token'); // O la forma en que guardes tu token
        return axios.get(USUARIO_BASE_REST_API_URL, { // Usa el endpoint correcto
            headers: {
                'Authorization': `Bearer ${token}` // Agrega el token al encabezado
            }
        }).then(res => {
            return res.data; // Devuelve los datos
        }).catch(error => {
            console.error("Error al obtener las personas:", error); // Manejo de errores
            throw error; // Opcional: vuelve a lanzar el error
        });
    }
}

export default PersonaService;
