import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import CoordenadasService from '../services/CoordenadasService'; // Asegúrate de importar el servicio

const MapComponent = () => {
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null); // Estado para el marcador seleccionado

  const fetchCoordenadas = async () => {
    try {
      const coordenadas = await CoordenadasService.getCoordenadas();
      console.log('Coordenadas recibidas:', coordenadas); // Verifica aquí

      // Asegúrate de que las coordenadas sean correctas
      const markersData = coordenadas.map(coord => ({
        id: coord.id,
        marca: coord.marca,
        lat: parseFloat(coord.latitud), // Cambiar a latitud
        lng: parseFloat(coord.longitud), // Cambiar a longitud
      }));

      // Comprueba los marcadores antes de establecer el estado
      console.log('Marcadores actuales:', markersData);
      setMarkers(markersData);
    } catch (error) {
      console.error('Error fetching coordinates:', error);
    }
  };

  useEffect(() => {
    fetchCoordenadas();
  }, []);

  const handleMarkerClick = (marker) => {
    setSelectedMarker(marker); // Actualiza el marcador seleccionado
  };

  const handleCloseInfoWindow = () => {
    setSelectedMarker(null); // Cierra el InfoWindow
  };

  return (
    <LoadScript googleMapsApiKey="AIzaSyBomnY9UemevV5IVEXtYHP6SWw3kRsERGc">
      <GoogleMap
        mapContainerStyle={{ height: '400px', width: '800px' }}
        center={{ lat: 4.610, lng: -74.083 }} // Ajusta al centro deseado
        zoom={6} // Aumenta el nivel de zoom si es necesario
      >
        {markers.map(marker => (
          <Marker 
            key={marker.id} 
            position={{ lat: marker.lat, lng: marker.lng }} 
            title={marker.marca} 
            onClick={() => handleMarkerClick(marker)} // Maneja el click en el marcador
          />
        ))}

        {selectedMarker && ( // Verifica si hay un marcador seleccionado
          <InfoWindow
            position={{ lat: selectedMarker.lat, lng: selectedMarker.lng }}
            onCloseClick={handleCloseInfoWindow} // Cierra el InfoWindow
          >
            <div>
              <h4>{selectedMarker.marca}</h4>
              {/* Aquí puedes añadir más información de la coordenada */}
              <p>ID: {selectedMarker.id}</p>
              <p>Latitud: {selectedMarker.lat}</p>
              <p>Longitud: {selectedMarker.lng}</p>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </LoadScript>
  );
};

export default MapComponent;
