import React, { useState } from 'react';

const RegistroPersona = () => {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [email, setEmail] = useState('');
  const [identificacion, setIdentificacion] = useState('');
  const [fechaNacimiento, setFechaNacimiento] = useState('');
  const [usuario, setUsuario] = useState({ login: '', password: '' });
  const [modalMessage, setModalMessage] = useState('');
  const [modalType, setModalType] = useState(''); // 'success' o 'error'
  const [showModal, setShowModal] = useState(false);

  const handleCloseModal = () => setShowModal(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validar que se complete la fecha de nacimiento (puedes agregar más validaciones aquí)
    if (!fechaNacimiento) {
      setModalMessage('La fecha de nacimiento es obligatoria.');
      setModalType('error');
      setShowModal(true);
      return;
    }

    const dataToSend = {
      nombre,
      apellido,
      email,
      identificacion,
      fechaNacimiento,
      usuario,
    };

    fetch("http://localhost:9001/Laboratorio1/persona", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((response) => {
        if (response.ok) {
          return response.json(); // Esto manejará el estado 201
        } else {
          return response.json().then((err) => {
            throw new Error(err.message || "Error en la solicitud.");
          });
        }
      })
      .then((data) => {
        // Manejo de éxito
        setModalMessage(data); // Asignar el mensaje de éxito
        setModalType("success");
        setShowModal(true);
      })
      .catch((error) => {
        console.error("Error:", error);
        setModalMessage(error.message); // Asignar el mensaje de error
        setModalType("error");
        setShowModal(true);
      });
  };

  return (
    <div className="container mt-5">
      <div className="card">
        <div className="card-header">
          <h2>Agregar Persona</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            {/* Formulario de registro */}
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Nombre:</label>
                <input
                  type="text"
                  name="pnombre"
                  className="form-control"
                  value={formData.pnombre}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Nombre:</label>
                <input
                  type="text"
                  name="snombre"
                  className="form-control"
                  value={formData.snombre}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Apellido:</label>
                <input
                  type="text"
                  name="papellido"
                  className="form-control"
                  value={formData.papellido}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Apellido:</label>
                <input
                  type="text"
                  name="sapellido"
                  className="form-control"
                  value={formData.sapellido}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Ubicación:</label>
                <input
                  type="text"
                  name="ubicacion"
                  className="form-control"
                  value={formData.ubicacion}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Identificación:</label>
                <input
                  type="number"
                  name="identificacion"
                  className="form-control"
                  value={formData.identificacion}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Fecha de Nacimiento:</label>
                <input
                  type="date"
                  name="fechaNacimiento"
                  className="form-control"
                  value={formData.fechaNacimiento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3>Usuario</h3>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Login:</label>
                <input
                  type="text"
                  name="usuario.login"
                  className="form-control"
                  value={formData.usuario.login}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Password:</label>
                <input
                  type="password"
                  name="usuario.password"
                  className="form-control"
                  value={formData.usuario.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <button type="submit" className="btn btn-primary">
              Guardar
            </button>
            <button
              type="button"
              className="btn btn-secondary ms-3"
              onClick={() => (window.location.href = "/login")}
            >
              Regresar a Login
            </button>
          </form>
        </div>
      </div>

      {/* Modal para mostrar mensajes */}
      {showModal && (
        <div className={`modal fade show`} style={{ display: "block" }} tabIndex="-1" role="dialog">
          <div className="modal-dialog" role="document">
            <div className={`modal-content ${modalType === "success" ? "bg-success text-white" : "bg-danger text-white"}`}>
              <div className="modal-header">
                <h5 className="modal-title">
                  {modalType === "success" ? "Éxito" : "Error"}
                </h5>
                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
              </div>
              <div className="modal-body">
                <p>{modalMessage}</p>
                </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                  Cerrar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Register;
