// src/services/AuthService.js

const API_BASE_URL = 'http://localhost:9001'; // Cambia esto si es necesario

export const AuthService = {
    // Método para iniciar sesión
    login: async (username, password) => {
        try {
            const response = await fetch(`${API_BASE_URL}/authenticate`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) {
                throw new Error('Error en el inicio de sesión');
            }

            const data = await response.json();
            return data; // Devuelve la respuesta, que puede contener el token u otros datos necesarios
        } catch (error) {
            console.error('Error en login:', error);
            throw error; // Propaga el error para que el componente que llama pueda manejarlo
        }
    },

    // Método para obtener la API Key
    fetchApiKey: async (username) => {
        try {
            const response = await fetch(`${API_BASE_URL}/apiKey`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'username': username,
                },
            });

            if (!response.ok) {
                throw new Error('Error al obtener la API Key');
            }

            const data = await response.json();
            return data.apiKey; // Devuelve la API Key
        } catch (error) {
            console.error('Error en fetchApiKey:', error);
            throw error; // Propaga el error
        }
    },
    
    // Puedes agregar más métodos relacionados con la autenticación si es necesario
};
