import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';


const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [apiKey, setApiKey] = useState(''); // Estado para la API Key
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError('');

        try {
            const response = await axios.post('http://localhost:9001/authenticate', { // Asegúrate de que esta URL sea correcta
                username,
                password,
            }, {
                headers: {
                    apikey: apiKey, // Envío de la API Key en los encabezados
                },
            });

            if (response.data && response.data.token) {
                localStorage.setItem('token', response.data.token);
                localStorage.setItem('apiKey', response.data.apikey); // Almacena la API Key
                navigate('/home');
            } else {
                setError('Respuesta del servidor inválida');
            }
        } catch (error) {
            if (error.response) {
                setError(error.response.data || 'Error en la autenticación');
            } else if (error.request) {
                setError('No se pudo conectar con el servidor');
            } else {
                setError('Error al procesar la solicitud');
            }
            console.error('Error al autenticar:', error);
        }
    };

    return (
        <div className="container-fluid vh-100 d-flex align-items-center justify-content-center" style={{ background: 'linear-gradient(45deg, #74ebd5, #acb6e5)', overflow: 'hidden' }}>
            <div className="card shadow" style={{ maxWidth: '400px', width: '100%', backgroundColor: '#ffffff', borderRadius: '10px' }}>
                <div className="card-header text-center">
                    <h2>Iniciar Sesión</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <InputText
                                type="text"
                                className="form-control mt-2"
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contraseña</label>
                            <InputText
                                type="password"
                                className="form-control mt-2"
                                id="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                autoComplete="current-password"
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="apikey">API Key</label>
                            <InputText
                                type="text"
                                className="form-control mt-2"
                                id="apikey"
                                placeholder="Ingresa tu API Key"
                                value={apiKey}
                                onChange={(e) => setApiKey(e.target.value)}
                                required
                            />
                        </div>
                        <Button 
                            type="submit" 
                            label="Iniciar Sesión" 
                            className="mt-3" 
                            style={{ width: '100%', backgroundColor: '#007bff', borderColor: '#007bff' }}
                        />
                        <button 
                            type="button" 
                            className="btn btn-link mt-3" 
                            onClick={() => navigate('/register')}
                        >
                            Registrarse
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
