import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import { useParams, Link } from 'react-router-dom'; 
import CoordenadasService from '../services/CoordenadasService';

const MapaIndividual = () => {
    const { idpersona } = useParams(); // Obtener el ID de la persona desde la URL
    console.log('ID de la persona:', idpersona); // Verifica que el ID esté definido
    const [marker, setMarker] = useState(null);
    const [selectedMarker, setSelectedMarker] = useState(null);

    const fetchCoordenadas = async () => {
        try {
            const coordenadas = await CoordenadasService.getCoordenadasIndividual(idpersona); // Llama al método correcto
            console.log('Coordenadas recibidas:', coordenadas);

            if (!coordenadas) {
                console.error('No se recibió una coordenada válida.');
                return;
            }

            const markerData = {
                id: coordenadas.idpersona,
                marca: coordenadas.marca,
                lat: parseFloat(coordenadas.latitud),
                lng: parseFloat(coordenadas.longitud),
            };

            setMarker(markerData); 
        } catch (error) {
            console.error('Error fetching coordinates:', error);
        }
    };

    useEffect(() => {
        fetchCoordenadas();
    }, [idpersona]); // Cambia de [id] a [idpersona]

    const center = marker ? { lat: marker.lat, lng: marker.lng } : { lat: 0, lng: 0 };

    return (
        <div style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100vh',
            background: 'linear-gradient(to bottom, #6a11cb, #2575fc)'
        }}>
            <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
                <GoogleMap
                    mapContainerStyle={{ height: '400px', width: '800px', borderRadius: '10px' }}
                    center={center}
                    zoom={12}
                >
                    {marker && (
                        <Marker
                            key={marker.id}
                            position={{ lat: marker.lat, lng: marker.lng }}
                            title={marker.marca}
                            onClick={() => setSelectedMarker(marker)}
                        />
                    )}

                    {selectedMarker && (
                        <InfoWindow
                            position={{ lat: selectedMarker.lat, lng: selectedMarker.lng }}
                            onCloseClick={() => setSelectedMarker(null)}
                        >
                            <div>
                                <h1>{selectedMarker.marca}</h1>
                                <p>ID: {selectedMarker.id}</p>
                                <p>Latitud: {selectedMarker.lat}</p>
                                <p>Longitud: {selectedMarker.lng}</p>
                            </div>
                        </InfoWindow>
                    )}
                </GoogleMap>
            </LoadScript>

            <div className="d-flex justify-content-center mt-4">
                <Link to="/home" className="btn btn-info">Volver Home</Link>
            </div>
        </div>
    );
};

export default MapaIndividual;
