import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import { Toast } from 'primereact/toast';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'primereact/resources/themes/saga-blue/theme.css'; // Elige el tema que prefieras
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [apiKey, setApiKey] = useState('');
    const navigate = useNavigate();
    const toastRef = React.useRef(null);

    useEffect(() => {
        fetchApiKey(); 
    }, [username]);

    const fetchApiKey = async () => {
        if (username) {
            try {
                const response = await axios.get(`http://localhost:9001/apiKey`, {
                    headers: {
                        username: username
                    }
                });
                setApiKey(response.data.apiKey);
                setError(''); 
            } catch (err) {
                console.error('Error al obtener la API Key:', err);
                setError('No se pudo recuperar la API Key');
            }
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        
        if (!apiKey) {
            setError('No se pudo recuperar la API Key');
            return;
        }

        try {
            const response = await axios.post('http://localhost:9001/authenticate', {
                username,
                password
            }, {
                headers: {
                    apikey: apiKey
                }
            });
            localStorage.setItem('token', response.data.token);
            navigate('/home');
        } catch (error) {
            setError('Credenciales inválidas');
            console.error('Error al autenticar:', error);
            toastRef.current.show({ severity: 'error', summary: 'Error', detail: 'Credenciales inválidas', life: 3000 });
        }
    };

    return (
        <div className="container mt-5">
            <div className="card" style={{ maxWidth: '400px', margin: 'auto' }}>
                <div className="card-header text-center">
                    <h2>Iniciar Sesión</h2>
                </div>
                <div className="card-body">
                    <form onSubmit={handleSubmit}>
                        {error && <div className="alert alert-danger">{error}</div>}
                        <div className="form-group">
                            <label htmlFor="username">Nombre de usuario</label>
                            <InputText
                                id="username"
                                placeholder="Ingresa tu nombre de usuario"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                                className="p-inputtext-sm w-100"
                            />
                        </div>
                        <div className="form-group mt-3">
                            <label htmlFor="password">Contraseña</label>
                            <InputText
                                id="password"
                                type="password"
                                placeholder="Ingresa tu contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                className="p-inputtext-sm w-100"
                            />
                        </div>
                        <div className="d-flex justify-content-between mt-3">
                            <Button type="submit" label="Iniciar Sesión" className="p-button-primary" />
                            <Button 
                                type="button" 
                                label="Registrarse" 
                                className="p-button-text" 
                                onClick={() => navigate('/register')}
                            />
                        </div>
                    </form>
                    <Toast ref={toastRef} />
                </div>
            </div>
        </div>
    );
};

export default Login;
