import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import './Register.css'; // Asegúrate de que el archivo CSS esté en el mismo directorio

function Register() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    primerNombre: "",
    segundoNombre: "",
    primerApellido: "",
    segundoApellido: "",
    identificacion: "",
    email: "",
    fechaNacimiento: "",
    ubicacion: {
      lugar: "",
      ciudad: "",
      departamento: "",
    },
    usuario: {
      login: "",
      password: "",
    },
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes(".")) {
      const [objectName, field] = name.split(".");
      setFormData((prevData) => ({
        ...prevData,
        [objectName]: {
          ...prevData[objectName],
          [field]: value,
        },
      }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const currentDate = new Date();
    const selectedDate = new Date(formData.fechaNacimiento);
    const year = selectedDate.getFullYear();

    if (selectedDate > currentDate) {
      alert("La fecha de nacimiento no puede ser futura.");
      return;
    }

    if (year < 1900 || year > 2100) {
      alert("El año de nacimiento debe estar entre 1900 y 2100.");
      return;
    }

    const dataToSend = {
      ...formData,
      identificacion: parseInt(formData.identificacion),
      usuario: {
        ...formData.usuario,
        id: {
          login: formData.usuario.login,
          persona: null, // Esto se establecerá en el backend
        },
      },
    };

    try {
      const response = await fetch("http://localhost:9001/Laboratorio1/persona", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(dataToSend),
      });

      let responseData;
      const contentType = response.headers.get("content-type");
      if (contentType && contentType.includes("application/json")) {
        responseData = await response.json();
      } else {
        responseData = await response.text();
      }

      if (response.ok) {
        setModalMessage(typeof responseData === "object" ? responseData.message : responseData);
        setModalType("success");
        setShowModal(true);
      } else {
        setModalMessage(typeof responseData === "object" ? responseData.message : responseData);
        setModalType("error");
        setShowModal(true);
      }
    } catch (error) {
      console.error("Error:", error);
      setModalMessage("Error en la solicitud.");
      setModalType("error");
      setShowModal(true);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    if (modalType === "success") {
      setFormData({
        primerNombre: "",
        segundoNombre: "",
        primerApellido: "",
        segundoApellido: "",
        identificacion: "",
        email: "",
        fechaNacimiento: "",
        ubicacion: {
          lugar: "",
          ciudad: "",
          departamento: "",
        },
        usuario: {
          login: "",
          password: "",
        },
      });
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-lg border-light">
        <div className="card-header text-center">
          <h2 className="text-dark">Registrar Persona</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Nombre:</label>
                <input
                  type="text"
                  name="primerNombre"
                  className="form-control"
                  value={formData.primerNombre}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Nombre:</label>
                <input
                  type="text"
                  name="segundoNombre"
                  className="form-control"
                  value={formData.segundoNombre}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Primer Apellido:</label>
                <input
                  type="text"
                  name="primerApellido"
                  className="form-control"
                  value={formData.primerApellido}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Segundo Apellido:</label>
                <input
                  type="text"
                  name="segundoApellido"
                  className="form-control"
                  value={formData.segundoApellido}
                  onChange={handleChange}
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Identificación:</label>
                <input
                  type="number"
                  name="identificacion"
                  className="form-control"
                  value={formData.identificacion}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Email:</label>
                <input
                  type="email"
                  name="email"
                  className="form-control"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Fecha de Nacimiento:</label>
                <input
                  type="date"
                  name="fechaNacimiento"
                  className="form-control"
                  value={formData.fechaNacimiento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3 className="mt-4">Ubicación</h3>
            <div className="row mb-3">
              <div className="col-md-4">
                <label className="form-label">Lugar:</label>
                <input
                  type="text"
                  name="ubicacion.lugar"
                  className="form-control"
                  value={formData.ubicacion.lugar}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-4">
                <label className="form-label">Ciudad:</label>
                <input
                  type="text"
                  name="ubicacion.ciudad"
                  className="form-control"
                  value={formData.ubicacion.ciudad}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-4">
                <label className="form-label">Departamento:</label>
                <input
                  type="text"
                  name="ubicacion.departamento"
                  className="form-control"
                  value={formData.ubicacion.departamento}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <h3 className="mt-4">Usuario</h3>
            <div className="row mb-3">
              <div className="col-md-6">
                <label className="form-label">Login:</label>
                <input
                  type="text"
                  name="usuario.login"
                  className="form-control"
                  value={formData.usuario.login}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Password:</label>
                <input
                  type="password"
                  name="usuario.password"
                  className="form-control"
                  value={formData.usuario.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-primary">
                Guardar
              </button>
              <button type="button" className="btn btn-secondary" onClick={() => navigate("/")}>
                Cancelar
              </button>
            </div>
          </form>

          {showModal && (
            <div className="modal show" style={{ display: "block" }}>
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">{modalType === "success" ? "Éxito" : "Error"}</h5>
                    <button type="button" className="btn-close" onClick={handleCloseModal}></button>
                  </div>
                  <div className="modal-body">
                    <p>{modalMessage}</p>
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                      Cerrar
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Register;
