const API_URL = 'http://localhost:9001/Laboratorio1';

const handleResponse = async (response) => {
  if (!response.ok) {
    const errorText = await response.text();
    console.error('Server response:', errorText);
    throw new Error(`Error en la solicitud: ${response.status} - ${errorText}`);
  }
  return response.json();
};

const PersonaService = {
  getHeaders: () => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No se encontró un token. Inicia sesión nuevamente.');
    }
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer eyJhbGciOiJIUzUxMiJ9.eyJqdGkiOiJQUE9PSUlfSldUIiwic3ViIjoibGl6ZXRoY2FybyIsImF1dGhvcml0aWVzIjpbIlJPTEVfVVNFUiJdLCJpYXQiOjE3Mjg3MDk4NjYsImV4cCI6MTcyOTU3Mzg2Nn0.eldgvkN0MnjzuK7PSktA7tk9oqdE--3N3PB08M4-AzWYTFFLMjAqUADZdJkrdXvHcwjVTH2IKyNfnuDE3oIYqw`,
    };
  },

  getHeadersForRegister: () => {
    return {
      'Content-Type': 'application/json',
    };
  },

  getAllPersons: async () => {
    try {
      const response = await fetch(`${API_URL}/personas`, {
        method: 'GET',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al obtener personas:', error);
      throw error;
    }
  },

  getPersonById: async (id) => {
    try {
      const response = await fetch(`${API_URL}/persona/id/${id}`, {
        method: 'GET',
        headers: PersonaService.getHeaders(),
      });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error al obtener persona con ID ${id}:`, error);
      throw error;
    }
  },

  registerUser: async (personData) => {
    try {
      console.log('Sending data:', JSON.stringify(personData, null, 2));
      const response = await fetch(`${API_URL}/persona`, {
        method: 'POST',
        headers: PersonaService.getHeadersForRegister(),
        body: JSON.stringify(personData),
      });
      
      if (!response.ok) {
        const errorText = await response.text();
        if (errorText.includes("nombre de usuario ya está registrado")) {
          throw new Error("El nombre de usuario ya está registrado");
        } else if (errorText.includes("correo ya está registrado")) {
          throw new Error("El correo electrónico ya está registrado");
        } else if (errorText.includes("identificación ya está registrada")) {
          throw new Error("La identificación ya está registrada");
        } else {
          throw new Error(errorText);
        }
      }
      
      return handleResponse(response);
    } catch (error) {
      console.error('Error al registrar usuario:', error);
      throw error;
    }
  },

  createPerson: async (personData) => {
    try {
      const response = await fetch(`${API_URL}/persona`, {
        method: 'POST',
        headers: PersonaService.getHeaders(),
        body: JSON.stringify(personData),
      });
      return handleResponse(response);
    } catch (error) {
      console.error('Error al crear persona:', error);
      throw error;
    }
  },

  updatePerson: async (id, personData) => {
    try {
      const response = await fetch(`${API_URL}/persona`, {
        method: 'PUT',
        headers: PersonaService.getHeaders(),
        body: JSON.stringify({ ...personData, idpersona: id }),
      });
      return handleResponse(response);
    } catch (error) {
      console.error(`Error al actualizar persona con ID ${id}:`, error);
      throw error;
    }
  },

  deletePerson: async (id) => {
    try {
      const response = await fetch(`${API_URL}/persona/${id}`, {
        method: 'DELETE',
        headers: PersonaService.getHeaders(),
      });
      if (!response.ok) {
